INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (2,'<p dir=\"ltr\">Ng&agrave;y xưa c&oacute; một ch&agrave;ng trai hiền l&agrave;nh, khỏe mạnh t&ecirc;n l&agrave; Khoai đi c&agrave;y thu&ecirc; cho vợ chồng &ocirc;ng ph&uacute; hộ. Hai người n&agrave;y c&oacute; hứa: \"Con chịu kh&oacute; l&agrave;m lụng gi&uacute;p ta, ba năm nữa ta sẽ gả con g&aacute;i ta cho\".</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">Tin v&agrave;o lời hứa, anh ra sức l&agrave;m việc kh&ocirc;ng ngại kh&oacute; nhọc. Nhưng ba năm sau, khi m&agrave; &ocirc;ng nh&agrave; gi&agrave;u đ&atilde; c&oacute; mọi thứ của cải, &ocirc;ng b&egrave;n trở mặt, &ocirc;ng đưa ra một điều kiện l&agrave; phải t&igrave;m được một c&acirc;y tre trăm đốt để l&agrave;m nh&agrave; cưới vợ th&igrave; &ocirc;ng mới gả con g&aacute;i cho. V&igrave; muốn c&oacute; cơ hội để cưới con g&aacute;i của chủ, anh b&egrave;n l&ecirc;n rừng quyết t&acirc;m t&igrave;m được một c&acirc;y tre trăm đốt. T&igrave;m m&atilde;i kh&ocirc;ng thấy, anh thất vọng ngồi kh&oacute;c. Bỗng Bụt hiện l&ecirc;n v&agrave; bảo anh đi t&igrave;m, chặt cho đủ 100 đốt tre rời rồi gi&uacute;p anh bằng hai c&acirc;u thần ch&uacute;: \"khắc nhập, khắc nhập!\" để gắn kết 100 đốt tre rời th&agrave;nh một c&acirc;y tre trăm đốt v&agrave; \"khắc xuất, khắc xuất!\" để t&aacute;ch c&aacute;c đốt tre ra.</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">Nhờ l&agrave; c&aacute;c kh&uacute;c tre rời n&ecirc;n anh cũng dễ d&agrave;ng g&aacute;nh về l&agrave;ng được. Về nh&agrave;, anh cho cha vợ tương lai của m&igrave;nh xem c&acirc;y tre trăm đốt. Kh&ocirc;ng tin v&agrave;o điều mắt m&igrave;nh nh&igrave;n thấy, &ocirc;ng nh&agrave; gi&agrave;u sờ tay v&agrave;o c&acirc;y đếm từng kh&uacute;c tre. Ph&eacute;p m&agrave;u của Bụt đ&atilde; h&uacute;t &ocirc;ng d&iacute;nh lu&ocirc;n v&agrave;o c&acirc;y tre. Khi &ocirc;ng đồng &yacute; giữ lời hứa, anh mới sực nhớ ra c&acirc;u \"khắc xuất\" để giải tho&aacute;t cho cha vợ của m&igrave;nh. Sợ h&atilde;i, &ocirc;ng ph&uacute; hộ phải giữ lời hứa, gả con g&aacute;i cho anh n&ocirc;ng d&acirc;n. Cuối c&ugrave;ng, anh v&agrave; con g&aacute;i &ocirc;ng ph&uacute; hộ sống với nhau hạnh ph&uacute;c m&atilde;i m&atilde;i.</p>\r\n<p>&nbsp;</p>','2022-05-30 16:21:00.000000','pe69c6af8-bd23-4766-9907-4c81d42eec38.jpg','Cây tre trăm đốt',1);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (3,'<p dir=\"ltr\">Ng&agrave;y xưa, c&oacute; một cậu b&eacute; được mẹ cưng chiều n&ecirc;n rất nghịch v&agrave; ham chơi. Một lần, bị mẹ mắng, cậu v&ugrave;ng vằng bỏ đi. Cậu la c&agrave; khắp nơi, mẹ cậu ở nh&agrave; kh&ocirc;ng biết cậu ở đ&acirc;u n&ecirc;n buồn lắm. Ng&agrave;y ng&agrave;y, mẹ ngồi tr&ecirc;n bậc cửa ng&oacute;ng cậu về. Một thời gian tr&ocirc;i qua m&agrave; cậu vẫn kh&ocirc;ng về. V&igrave; qu&aacute; đau buồn v&agrave; kiệt sức, mẹ cậu gục xuống... Kh&ocirc;ng biết cậu đ&atilde; đi bao l&acirc;u. Một h&ocirc;m, vừa đ&oacute;i vừa r&eacute;t, lại bị trẻ lớn hơn đ&aacute;nh, cậu mới nhớ đến mẹ: &ldquo;Phải rồi, khi m&igrave;nh đ&oacute;i, mẹ vẫn cho m&igrave;nh ăn, khi m&igrave;nh bị đứa kh&aacute;c bắt nạt, mẹ vẫn b&ecirc;nh m&igrave;nh, về với mẹ th&ocirc;i&rdquo;. Cậu liền t&igrave;m đường về nh&agrave;... Ở nh&agrave;, cảnh vật vẫn như xưa, nhưng kh&ocirc;ng thấy mẹ đ&acirc;u. Cậu khản tiếng gọi mẹ:</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">&ldquo;Mẹ ơi, mẹ đi - đ&acirc;u rồi, con đ&oacute;i qu&aacute; !&rdquo; Cậu b&eacute; gục xuống, rồi &ocirc;m một c&acirc;y xanh trong vườn m&agrave; kh&oacute;c. Kỳ lạ thay, c&acirc;y xanh bỗng run rẩy. Từ c&aacute;c c&agrave;nh l&aacute;, những đ&agrave;i hoa b&eacute; t&iacute; trổ ra, nở trắng như m&acirc;y. Hoa t&agrave;n, quả xuất hiện, lớn nhanh, da căng mịn, xanh &oacute;ng &aacute;nh. C&acirc;y nghi&ecirc;ng c&agrave;nh, một quả to rơi v&agrave;o tay cậu b&eacute;. Cậu b&eacute; cắn một miếng thật to. Ch&aacute;t qu&aacute; Quả thứ hai rơi xuống. Cậu lột vỏ, cắn v&agrave;o hạt quả. Cứng qu&aacute;. Quả thứ ba rơi xuống. Cậu khẽ b&oacute;p quanh quanh quả, lớp vỏ mềm dần rồi khẽ nứt ra một kẽ nhỏ. Một d&ograve;ng sữa trắng s&oacute;ng s&aacute;nh tr&agrave;o ra, ngọt thơm như sữa mẹ... Cậu b&eacute; gh&eacute; m&ocirc;i hứng lấy d&ograve;ng sữa ngọt ng&agrave;o, thơm ngon như sữa mẹ. C&acirc;y rung rinh c&agrave;nh l&aacute;, th&igrave; th&agrave;o:</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">&ldquo;Ăn tr&aacute;i ba lần mới biết tr&aacute;i ngon</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">Con c&oacute; lớn kh&ocirc;n mới hay l&ograve;ng mẹ&rdquo;</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">Cậu o&agrave; l&ecirc;n kh&oacute;c. Nhận ra mẹ đ&atilde; kh&ocirc;ng c&ograve;n nữa. Cậu nh&igrave;n l&ecirc;n t&aacute;n l&aacute;, l&aacute; một mặt xanh b&oacute;ng, mặt kia đỏ hoe như mắt mẹ kh&oacute;c chờ con. Cậu &ocirc;m lấy th&acirc;n c&acirc;y m&agrave; kh&oacute;c, th&acirc;n c&acirc;y x&ugrave; x&igrave;, th&ocirc; r&aacute;p như đ&ocirc;i b&agrave;n tay l&agrave;m lụng của mẹ. Nước mắt cậu rơi xuống gốc c&acirc;y. C&acirc;y x&ograve;a c&agrave;nh &ocirc;m cậu, rung rinh c&agrave;nh l&aacute; như tay mẹ &acirc;u yếm vỗ về. Cậu kể cho mọi người nghe chuyện về người mẹ v&agrave; nỗi &acirc;n hận của m&igrave;nh&hellip; Tr&aacute;i c&acirc;y thơm ngon ở vườn nh&agrave; cậu, ai cũng th&iacute;ch. Họ đem về gieo trồng khắp nơi v&agrave; đặt t&ecirc;n l&agrave; C&acirc;y V&uacute; Sữa.</p>\r\n<p>&nbsp;</p>','2022-05-30 16:23:00.000000','p87ac876f-fbd1-47bb-bf29-31db15f84600.jpg','Sự tích cây vú sữa',1);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (5,'<p dir=\"ltr\">Tấm v&agrave; C&aacute;m l&agrave; hai chị em c&ugrave;ng cha kh&aacute;c mẹ. Bố mẹ mất sớm, Tấm phải ở c&ugrave;ng với d&igrave; ghẻ (mẹ của C&aacute;m). Trong khi C&aacute;m được n&acirc;ng niu chiều chuộng th&igrave; Tấm lại phải vất vả khổ cực, bị d&igrave; la mắng v&agrave; đối xử thậm tệ. \"Tấm phải l&agrave;m lụng lu&ocirc;n canh, hết chăn tr&acirc;u, g&aacute;nh nước, đến th&aacute;i khoai, vớt b&egrave;o; đ&ecirc;m lại c&ograve;n xay l&uacute;a gi&atilde; gạo m&agrave; kh&ocirc;ng hết việc\".</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">Một h&ocirc;m, theo lời dặn của d&igrave;, C&aacute;m c&ugrave;ng Tấm ra ngo&agrave;i đồng bắt t&eacute;p. V&igrave; phần thưởng l&agrave; chiếc yếm đỏ hằng ao ước n&ecirc;n Tấm rất chăm chỉ miệt m&agrave;i bắt t&ocirc;m t&eacute;p giữa trời nắng oi bức. Trong khi đ&oacute;, C&aacute;m th&igrave; mải rong chơi đuổi hoa bắt bướm rồi qu&ecirc;n mất nhiệm vụ, kết quả l&agrave; chiều về giỏ kh&ocirc;ng c&oacute; g&igrave; cả. C&aacute;m lừa Tấm xuống tắm ao v&agrave; cướp hết số t&ocirc;m t&eacute;p m&agrave; Tấm đ&atilde; chăm chỉ nỗ lực cả buổi mới bắt được. Tấm tủi th&acirc;n v&agrave; ngồi kh&oacute;c một m&igrave;nh. Bụt thấy thương qu&aacute;, hiện l&ecirc;n an ủi gi&uacute;p đỡ, bảo Tấm mang con c&aacute; bống c&ograve;n s&oacute;t lại về nu&ocirc;i. Tấm hằng ng&agrave;y chừa một phần cơm của m&igrave;nh, ra giếng gọi c&aacute; bống &ldquo; Bống bống bang bang l&ecirc;n ăn cơm v&agrave;ng cơm bạc nh&agrave; ta chớ ăn cơm hẩm ch&aacute;o hoa nh&agrave; người&rdquo; v&agrave; lập tức c&aacute; hiện l&ecirc;n, đớp những hạt cơm v&agrave;ng ngọc chắt chiu của c&ocirc; Tấm. Mẹ con C&aacute;m mưu m&ocirc; xảo quyệt v&igrave; ganh gh&eacute;t n&ecirc;n r&igrave;nh được v&agrave; hại chết con c&aacute; bống. Lần n&agrave;y, Bụt lại ra tay gi&uacute;p đỡ bằng c&aacute;ch chỉ Tấm bỏ xương c&aacute; v&agrave;o lọ rồi đem cất dưới ch&acirc;n giường.</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">Nh&agrave; vua mở hội linh đ&igrave;nh, ai ai cũng n&aacute;o nức đi trẩy hội. Mẹ con C&aacute;m kh&ocirc;ng muốn Tấm đi dự hội b&egrave;n dở &acirc;m mưu bắt tấm nhặt một th&uacute;ng th&oacute;c v&agrave; gạo trộn lẫn. Bụt hiện l&ecirc;n, sai đ&agrave;n chim sẻ nhặt gi&uacute;p Tấm. Rồi bảo Tấm đ&agrave;o bốn c&aacute;i chậu xương c&aacute; dưới giường l&ecirc;n. Bất ngờ thay, những mảnh xương c&aacute; từ trước đ&atilde; biến th&agrave;nh trang phục lộng lẫy, tỏa s&aacute;ng,vừa vặn với th&acirc;n h&igrave;nh của Tấm v&agrave; n&agrave;ng vui vẻ đi tham dự lễ hội.</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">Trong l&uacute;c vội v&agrave;ng trở về, chẳng may Tấm sơ &yacute; đ&aacute;nh rơi một chiếc gi&agrave;y xuống s&ocirc;ng. Tấm m&ograve; m&atilde;i m&agrave; kh&ocirc;ng được. Vua nh&igrave;n thấy chiếc gi&agrave;y nhỏ nhắn xinh đẹp v&agrave; cảm l&ograve;ng với chủ nh&acirc;n, lập tức ra lệnh ai ướm thử vừa chiếc gi&agrave;y sẽ trở th&agrave;nh ho&agrave;ng hậu. Tất cả mọi người trong kinh th&agrave;nh khi nghe tin đều h&aacute;o hức n&ocirc; nức đến thử gi&agrave;y, t&igrave;m kiếm hi vọng trở th&agrave;nh vợ của người đứng đầu đất nước. Nhưng rất tiếc, tất cả đều kh&ocirc;ng vừa. Ri&ecirc;ng đến lượt Tấm, đ&ocirc;i gi&agrave;y vừa kh&iacute;t. Vua cho mời n&agrave;ng về cung v&agrave; Tấm trở th&agrave;nh ho&agrave;ng hậu.</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">Mẹ con C&aacute;m sau bao nhi&ecirc;u chuyện vẫn căm th&ugrave; Tấm v&agrave; &acirc;m mưu h&atilde;m hại. Trong một dịp Tấm về giỗ cha, họ đ&atilde; l&eacute;n l&uacute;t chặt đứt c&acirc;y cau v&agrave; hại chết Tấm. C&aacute;m v&agrave;o cung tiến vua thay chị. Tấm lần lượt biến th&agrave;nh con chim v&agrave;ng anh, c&acirc;y xoan, khung cửi, quả thị. Cuối c&ugrave;ng trở th&agrave;nh con g&aacute;i của b&agrave; l&atilde;o h&agrave;ng nước. Khi nh&agrave; vua t&igrave;nh cờ đi qua đ&acirc;y v&agrave; nhận ra Tấm, liền đ&oacute;n n&agrave;ng về cung. Kết th&uacute;c c&acirc;u chuyện, mẹ con C&aacute;m bị trừng phạt th&iacute;ch đ&aacute;ng v&agrave; Tấm sống hạnh ph&uacute;c m&atilde;i m&atilde;i b&ecirc;n nh&agrave; vua.</p>\r\n<p>&nbsp;</p>','2022-05-31 10:50:33.958000','p48bdcea9-ccbb-4f9f-9a0c-ea690b6ea83d.jpg','Tấm Cám',1);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (6,'<p dir=\"ltr\">Ng&agrave;y xưa c&oacute; một c&ocirc; b&eacute; sống c&ugrave;ng mẹ trong một t&uacute;p lều tranh dột n&aacute;t nhưng đ&oacute; l&agrave; một b&eacute; g&aacute;i v&ocirc; c&ugrave;ng hiếu thảo. Thật kh&ocirc;ng may mẹ của c&ocirc; b&eacute; lại bị bệnh rất nặng nhưng v&igrave; nh&agrave; ngh&egrave;o n&ecirc;n ko c&oacute; tiền mua thuốc chữa, v&agrave; c&ocirc; b&eacute; v&ocirc; c&ugrave;ng buồn b&atilde;.</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">Một lần đang ngồi kh&oacute;c b&ecirc;n đường bỗng c&oacute; một &ocirc;ng l&atilde;o đi qua thấy lạ b&egrave;n đừng lại hỏi khi biết sự t&igrave;nh &ocirc;ng gi&agrave; n&oacute;i với c&ocirc; b&eacute;:</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">&ndash; Ch&aacute;u h&atilde;y v&agrave;o rừng v&agrave; đến b&ecirc;n gốc c&acirc;y cổ thụ to nhất trong rừng h&aacute;i l&acirc;y một b&ocirc;ng hoa duy nhất tr&ecirc;n đ&oacute;. B&ocirc;ng hoa ấy c&oacute; bao nhi&ecirc;u c&aacute;nh th&igrave; tức l&agrave; mẹ ch&aacute;u sống được bằng đấy ng&agrave;y.</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">C&ocirc; b&eacute; liền v&agrave;o rừng v&agrave; rất l&acirc;u sau mới t&igrave;m thấy b&ocirc;ng hoa trắng đ&oacute;. Phải kh&oacute; khăn lắm c&ocirc; mới tr&egrave;o l&ecirc;n được để lấy b&ocirc;ng hoa, nhưng khi đếm chỉ c&oacute; một c&aacute;nh hai c&aacute;nh ba c&aacute;nh bốn c&aacute;nh. Chỉ c&oacute; bốn c&aacute;nh hoa l&agrave; sao chứ? Chẳng nhẽ mẹ c&ocirc; chỉ sống được b&agrave;ng đấy ng&agrave;y th&ocirc;i sao? Kh&ocirc;ng đ&agrave;nh l&ograve;ng c&ocirc; liền d&ugrave;ng tay x&eacute; nhẹ dần từng c&aacute;nh hoa lớn th&agrave;nh những c&aacute;nh hoa nhỏ v&agrave; b&ocirc;ng hoa cũng theo đ&oacute; m&agrave; nhiều c&aacute;nh dần l&ecirc;n nhiều đến mức kh&ocirc;ng c&ograve;n đếm được nữa. Từ đ&oacute; người đời gọi b&ocirc;ng hoa ấy l&agrave; b&ocirc;ng hoa c&uacute;c trắng để n&oacute;i về l&ograve;ng hiếu thảo của c&ocirc; b&eacute; đ&oacute; d&agrave;nh cho mẹ m&igrave;nh.</p>\r\n<p>&nbsp;</p>','2022-05-31 10:53:32.039000','p7859fb76-c82a-4e3d-80b8-bf5a9948d81a.png','Sự tích bông hoa cúc trắng',1);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (7,'<p>Ng&agrave;y xưa, c&oacute; hai vợ chồng một l&atilde;o n&ocirc;ng ngh&egrave;o đi ở cho nh&agrave; một ph&uacute; &ocirc;ng. Họ hiền l&agrave;nh, chăm chỉ nhưng đ&atilde; ngo&agrave;i năm mươi tuổi m&agrave; chưa c&oacute; lấy một mụn con.<br>Một h&ocirc;m, người vợ v&agrave;o rừng lấy củi. Trời nắng to, kh&aacute;t nước qu&aacute;, thấy c&aacute;i sọ dừa b&ecirc;n gốc c&acirc;y to đựng đầy nước mưa, b&agrave; b&egrave;n bưng l&ecirc;n uống. Thế rồi, về nh&agrave;, b&agrave; c&oacute; mang.<span id=\"more-173\"></span><br>&Iacute;t l&acirc;u sau, người chồng mất. B&agrave; sinh ra một đứa con kh&ocirc;ng c&oacute; ch&acirc;n tay, m&igrave;nh mẩy, cứ tr&ograve;n l&ocirc;ng lốc như một quả dừa. B&agrave; buồn, toan vứt n&oacute; đi th&igrave; đứa b&eacute; l&ecirc;n tiếng bảo.<br>&ndash; Mẹ ơi! Con l&agrave; người đấy! Mẹ đừng vứt con m&agrave; tội nghiệp. B&agrave; l&atilde;o thương t&igrave;nh để lại nu&ocirc;i rồi đặt t&ecirc;n cho cậu l&agrave; Sọ Dừa.</p>\r\n<p>Lớn l&ecirc;n, Sọ Dừa vẫn thế, cứ lăn l&ocirc;ng lốc chẳng l&agrave;m được việc g&igrave;. B&agrave; mẹ lấy l&agrave;m phiền l&ograve;ng lắm. Sọ Dừa biết vậy b&egrave;n xin mẹ đến chăn b&ograve; cho nh&agrave; ph&uacute; &ocirc;ng.</p>\r\n<p>Nghe n&oacute;i đến Sọ Dừa, ph&uacute; &ocirc;ng ngần ngại. Nhưng nghĩ: nu&ocirc;i n&oacute; th&igrave; &iacute;t tốn cơm, c&ocirc;ng s&aacute; lại chẳng đ&aacute;ng l&agrave; bao, ph&uacute; &ocirc;ng đồng &yacute;. Chẳng ngờ cậu chăn b&ograve; rất giỏi. Ng&agrave;y ng&agrave;y, cậu lăn sau đ&agrave;n b&ograve; ra đồng, tối đến lại lăn sau đ&agrave;n b&ograve; về nh&agrave;. Cả đ&agrave;n b&ograve;, con n&agrave;o con nấy cứ no căng. Ph&uacute; &ocirc;ng lấy l&agrave;m mừng lắm!</p>\r\n<p>V&agrave;o ng&agrave;y m&ugrave;a, t&ocirc;i tớ ra đồng l&agrave;m hết cả, ph&uacute; &ocirc;ng b&egrave;n sai ba c&ocirc; con g&aacute;i thay phi&ecirc;n nhau đem cơm cho Sọ Dừa. Trong những lần như thế, hai c&ocirc; chị ki&ecirc;u k&igrave;, &aacute;c nghiệt thường hắt hủi Sọ Dừa, chỉ c&oacute; c&ocirc; em vốn t&iacute;nh thương người l&agrave; đối đ&atilde;i với Sọ Dừa tử tế.</p>\r\n<p>Một h&ocirc;m đến phi&ecirc;n c&ocirc; &uacute;t mang cơm cho Sọ Dừa. Mới đến ch&acirc;n n&uacute;i, c&ocirc; bỗng nghe thấy tiếng s&aacute;o v&eacute;o von. R&oacute;n r&eacute;n bước l&ecirc;n c&ocirc; nh&igrave;n thấy một ch&agrave;ng trai kh&ocirc;i ng&ocirc; tuấn t&uacute; đang ngồi tr&ecirc;n chiếc v&otilde;ng đ&agrave;o thổi s&aacute;o cho đ&agrave;n b&ograve; gặm cỏ. Thế nhưng vừa mới đứng l&ecirc;n, tất cả đ&atilde; biến mất tăm, chỉ thấy Sọ Dừa nằm lăn l&oacute;c ở đấy. Nhiều lần như vậy, c&ocirc; &uacute;t biết Sọ Dừa kh&ocirc;ng phải người thường, b&egrave;n đem l&ograve;ng y&ecirc;u qu&yacute;.</p>\r\n<p>Đến cuối m&ugrave;a ở thu&ecirc;, Sọ Dừa về nh&agrave; giục mẹ đến hỏi con g&aacute;i ph&uacute; &ocirc;ng về l&agrave;m vợ. B&agrave; l&atilde;o thấy vậy tỏ ra v&ocirc; c&ugrave;ng sửng sốt, nhưng thấy con năn nỉ m&atilde;i, b&agrave; cũng chiều l&ograve;ng.<br>Thấy mẹ Sọ Dừa mang cau đến dạm, ph&uacute; &ocirc;ng cười mỉa mai:<br>&ndash; Muốn hỏi con g&aacute;i ta, h&atilde;y về sắm đủ một chĩnh v&agrave;ng cốm, mười tấm lụa đ&agrave;o, mười con lợn b&eacute;o, mười v&ograve; rượu tăm đem sang đ&acirc;y.</p>\r\n<p>B&agrave; l&atilde;o đ&agrave;nh ra về, nghĩ l&agrave; phải th&ocirc;i hẳn việc lấy vợ cho con. Chẳng ngờ, đ&uacute;ng ng&agrave;y hẹn, bỗng dưng trong nh&agrave; c&oacute; đầy đủ mọi s&iacute;nh lễ, lại c&oacute; cả gia nh&acirc;n ở dưới nh&agrave; chạy l&ecirc;n khi&ecirc;ng lễ vật sang nh&agrave; của ph&uacute; &ocirc;ng. Ph&uacute; &ocirc;ng hoa cả mắt l&uacute;ng t&uacute;ng gọi ba c&ocirc; con g&aacute;i ra hỏi &yacute;. Hai c&ocirc; chị bĩu m&ocirc;i ch&ecirc; bai Sọ Dừa xấu x&iacute; rồi ng&uacute;ng nguẩy đi v&agrave;o, chỉ c&oacute; c&ocirc; &uacute;t l&agrave; c&uacute;i đầu e lệ tỏ &yacute; bằng l&ograve;ng.</p>\r\n<p>Trong ng&agrave;y cưới, Sọ Dừa cho b&agrave;y cỗ thật linh đ&igrave;nh, gia nh&acirc;n chạy ra chạy v&agrave;o tấp nập. L&uacute;c rước d&acirc;u, chẳng ai thấy Sọ Dừa trọc lốc, xấu x&iacute; đ&acirc;u chỉ thấy một ch&agrave;ng trai kh&ocirc;i ng&ocirc; tuấn t&uacute; đứng b&ecirc;n c&ocirc; &uacute;t. Mọi người thấy vậy đều cảm thấy sửng sốt v&agrave; mừng rỡ, c&ograve;n hai c&ocirc; chị th&igrave; vừa tiếc lại vừa ghen tức.</p>\r\n<p>Từ ng&agrave;y ấy, hai vợ chồng Sọ Dừa sống với nhau rất hạnh ph&uacute;c. Kh&ocirc;ng những thế, Sọ Dừa c&ograve;n tỏ ra rất th&ocirc;ng minh. Ch&agrave;ng ng&agrave;y đ&ecirc;m miệt m&agrave;i đ&egrave;n s&aacute;ch v&agrave; quả nhi&ecirc;n năm ấy, Sọ Dừa đỗ trạng nguy&ecirc;n. Thế nhưng cũng lại chẳng bao l&acirc;u sau, Sọ Dừa được vua sai đi sứ. Trước khi đi, ch&agrave;ng đưa cho vợ một h&ograve;n đ&aacute; lửa, một con dao v&agrave; hai quả trứng g&agrave; n&oacute;i l&agrave; để hộ th&acirc;n.</p>\r\n<p>Ganh tị với c&ocirc; em, hai c&ocirc; chị sinh l&ograve;ng ghen gh&eacute;t rắp t&acirc;m hại em để thay l&agrave;m b&agrave; trạng. Nh&acirc;n quan trạng đi vắng, hai chị sang rủ c&ocirc; &uacute;t ch&egrave;o thuyền ra biển rồi cứ thế lừa đẩy c&ocirc; em xuống nước. C&ocirc; &uacute;t bị c&aacute; k&igrave;nh nuốt chửng, nhưng may c&oacute; con dao m&agrave; tho&aacute;t chết. C&ocirc; dạt v&agrave;o một h&ograve;n đảo, lấy dao kho&eacute;t bụng c&aacute; chui ra, đ&aacute;nh đ&aacute; lấy lửa nướng thịt c&aacute; ăn. Sống được &iacute;t ng&agrave;y tr&ecirc;n đảo, hai quả trứng g&agrave; cũng kịp nở th&agrave;nh một đ&ocirc;i g&agrave; đẹp để l&agrave;m bạn c&ugrave;ng c&ocirc; &uacute;t.</p>\r\n<p>Một h&ocirc;m c&oacute; chiếc thuyền đi qua đảo, con g&agrave; trống nh&igrave;n thấy b&egrave;n g&aacute;y to:<br>&ograve;&hellip; &oacute;&hellip; o<br>Phải thuyền quan trạng rước c&ocirc; t&ocirc;i về.<br>Quan cho thuyền v&agrave;o xem, chẳng ngờ đ&oacute; ch&iacute;nh l&agrave; vợ m&igrave;nh. Hai vợ chồng gặp nhau, mừng mừng tủi tủi. Đưa vợ về nh&agrave;, quan trạng mở tiệc mừng mời b&agrave; con đến chia vui, nhưng lại giấu vợ trong nh&agrave; kh&ocirc;ng cho ai biết. Hai c&ocirc; chị thấy thế khấp khởi mừng thầm, tranh nhau kể chuyện c&ocirc; em rủi ro ra chiều thương tiếc lắm. Quan trạng kh&ocirc;ng n&oacute;i g&igrave;, tiệc xong mới cho gọi vợ ra. Hai c&ocirc; chị nh&igrave;n thấy c&ocirc; em th&igrave; xấu hổ qu&aacute;, l&eacute;n bỏ ra về rồi từ đ&oacute; bỏ đi biệt xứ.</p>','2022-05-31 10:54:51.923000','pa45fc6b7-00e2-4fe0-9cbd-aa7efe14d9bd.jpg','Sọ dừa',1);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (8,'<p>Ng&agrave;y xưa, c&oacute; một bạn t&ecirc;n l&agrave; T&iacute;ch Chu. Bố mẹ T&iacute;ch Chu mất sớm, T&iacute;ch Chu ở với b&agrave;.</p>\r\n<p>H&agrave;ng ng&agrave;y b&agrave; phải l&agrave;m việc quần quật kiếm tiền nu&ocirc;i&nbsp;<a href=\"http://truyencotich.vn/\" target=\"_blank\" rel=\"noopener\">T&iacute;ch Chu</a>, c&oacute; thức g&igrave; ngon b&agrave; cũng d&agrave;nh cho T&iacute;ch Chu. Ban đ&ecirc;m, khi T&iacute;ch Chu ngủ th&igrave; b&agrave; thức quạt. Thấy b&agrave; thương T&iacute;ch Chu, c&oacute; người bảo:</p>\r\n<p>&ndash; B&agrave; ơi! L&ograve;ng b&agrave; thương T&iacute;ch Chu cao hơn trời, rộng hơn biển. Lớn l&ecirc;n,&nbsp;<a href=\"http://truyencotich.vn/\" target=\"_blank\" rel=\"noopener\">T&iacute;ch Chu</a>&nbsp;sẽ kh&ocirc;ng khi n&agrave;o qu&ecirc;n ơn b&agrave;.</p>\r\n<p>Thế nhưng lớn l&ecirc;n, T&iacute;ch Chu lại chẳng thương b&agrave;. B&agrave; th&igrave; suốt ng&agrave;y l&agrave;m việc vất vả, c&ograve;n T&iacute;ch Chu suốt ng&agrave;y rong chơi. V&igrave; l&agrave;m việc vất vả, ăn uống lại kham khổ n&ecirc;n b&agrave; bị ốm. B&agrave; l&ecirc;n cơn sốt nhưng chẳng ai tr&ocirc;ng nom. T&iacute;ch Chu m&atilde;i rong chơi với bạn b&egrave;, chẳng nghĩ g&igrave; đến b&agrave; đang ốm. Một buổi trưa, trời n&oacute;ng nực, cơn sốt l&ecirc;n cao, b&agrave; kh&aacute;t nước qu&aacute; liền gọi:</p>\r\n<p>&ndash; T&iacute;ch Chu ơi, cho b&agrave; ngụm nước. B&agrave; kh&aacute;t kh&ocirc; cổ rồi!</p>\r\n<p>B&agrave; gọi một lần, hai lần&hellip;rồi ba lần nhưng vẫn kh&ocirc;ng thấy T&iacute;ch Chu đ&aacute;p lại. M&atilde;i sau T&iacute;ch Chu thấy đ&oacute;i mới chạy về nh&agrave; kiếm c&aacute;i ăn. T&iacute;ch Chu ngạc nhi&ecirc;n hết sức thấy b&agrave; biến th&agrave;nh chim v&agrave; vỗ c&aacute;nh bay l&ecirc;n trời. T&iacute;ch Chu hoảng qu&aacute; k&ecirc;u l&ecirc;n:</p>\r\n<p>&ndash; B&agrave; ơi! B&agrave; đi đ&acirc;u? B&agrave; ở lại với ch&aacute;u. Ch&aacute;u sẽ mang nước cho b&agrave;, b&agrave; ơi!</p>\r\n<p>&ndash; C&uacute;c cu &hellip; cu! C&uacute;c &hellip; cu cu! Chậm mất rồi ch&aacute;u ạ, b&agrave; kh&aacute;t qu&aacute; kh&ocirc;ng thể chịu nổi phải h&oacute;a th&agrave;nh chim để bay đi kiếm nước. B&agrave; đi đ&acirc;y, b&agrave; kh&ocirc;ng về nữa đ&acirc;u!</p>\r\n<p>N&oacute;i rồi chim vỗ c&aacute;nh bay đi.&nbsp;<a href=\"http://truyencotich.vn/\" target=\"_blank\" rel=\"noopener\">T&iacute;ch Chu</a>&nbsp;hoảng qu&aacute; chạy theo b&agrave;, cứ nhằm theo hướng chim bay m&agrave; chạy. Cuối c&ugrave;ng T&iacute;ch Chu gặp chim đang uống nước ở một d&ograve;ng suối m&aacute;t. T&iacute;ch Chu gọi:</p>\r\n<p>&ndash; B&agrave; ơi! B&agrave; trở về với ch&aacute;u đi. Ch&aacute;u sẽ đi lấy nước cho b&agrave;, ch&aacute;u sẽ gi&uacute;p đỡ b&agrave;, ch&aacute;u sẽ kh&ocirc;ng l&agrave;m b&agrave; buồn nữa!</p>\r\n<p>&ndash; C&uacute;c &hellip;cu&hellip;cu, muộn qu&aacute; rồi ch&aacute;u ơi! B&agrave; kh&ocirc;ng trở lại được nữa đ&acirc;u!</p>\r\n<p>Nghe chim n&oacute;i, T&iacute;ch Chu &ograve;a kh&oacute;c,&nbsp;<a href=\"http://truyencotich.vn/\" target=\"_blank\" rel=\"noopener\">T&iacute;ch Chu</a>&nbsp;thương b&agrave; v&agrave; hối hận. Giữa l&uacute;c đ&oacute;, c&oacute; một b&agrave; ti&ecirc;n hiện ra, b&agrave; bảo T&iacute;ch Chu:</p>\r\n<p>&ndash; Nếu ch&aacute;u muốn b&agrave; trở lại th&agrave;nh người th&igrave; ch&aacute;u phải đi lấy nước suối Ti&ecirc;n cho b&agrave; ch&aacute;u uống. Đường l&ecirc;n suối Ti&ecirc;n xa lắm, ch&aacute;u c&oacute; đi được kh&ocirc;ng?</p>\r\n<p>Nghe b&agrave; Ti&ecirc;n n&oacute;i, T&iacute;ch Chu mừng rỡ v&ocirc; c&ugrave;ng, vội v&agrave;ng hỏi đường đến suối Ti&ecirc;n, rồi chẳng một ph&uacute;t chần chừ,&nbsp;<a href=\"http://truyencotich.vn/\" target=\"_blank\" rel=\"noopener\">T&iacute;ch Chu&nbsp;</a>hăng h&aacute;i đi ngay.</p>\r\n<p>Trải qua nhiều ng&agrave;y đ&ecirc;m lặn lội tr&ecirc;n đường, vượt qua rất nhiều nguy hiểm, cuối c&ugrave;ng T&iacute;ch Chu đ&atilde; lấy được nước suối mang về cho b&agrave; uống. Được uống nước suối Ti&ecirc;n, b&agrave; T&iacute;ch Chu trở lại th&agrave;nh người v&agrave; về ở với T&iacute;ch Chu.</p>\r\n<p>Từ đấy,&nbsp;<a href=\"http://truyencotich.vn/\" target=\"_blank\" rel=\"noopener\">T&iacute;ch Chu</a>&nbsp;hết l&ograve;ng y&ecirc;u thương chăm s&oacute;c b&agrave;.</p>','2022-05-31 10:56:33.221000','p09a71591-a245-45d3-b8c5-60f647dadd69.jpg','Tích Chu',1);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (9,'<p dir=\"ltr\">- Thu hoạch kh&aacute; chứ anh bạn?</p>\r\n<p dir=\"ltr\">- Qu&aacute; tốt l&agrave; đằng kh&aacute;c! - Thomas cười lớn - Anh kh&ocirc;ng tưởng tượng được đ&acirc;u, h&ocirc;m qua t&ocirc;i c&acirc;u được hơn 10 kg c&aacute; hồi ở đ&acirc;y lu&ocirc;n đấy!</p>\r\n<p dir=\"ltr\">Người đ&agrave;n &ocirc;ng cười cười:</p>\r\n<p dir=\"ltr\">- Anh biết m&igrave;nh đang n&oacute;i chuyện với ai chứ?</p>\r\n<p dir=\"ltr\">- Kh&ocirc;ng! - Thomas nghi hoặc đ&aacute;p.</p>\r\n<p dir=\"ltr\">Người đ&agrave;n &ocirc;ng nghi&ecirc;m mặt:</p>\r\n<p dir=\"ltr\">- Bảo vệ mới tại khu vực n&agrave;y!</p>\r\n<p dir=\"ltr\">- &Agrave;, vậy chắc anh chưa biết t&ocirc;i l&agrave; ai rồi! - Thomas cười lớn.</p>\r\n<p dir=\"ltr\">Vi&ecirc;n bảo vệ hỏi:</p>\r\n<p dir=\"ltr\">- Anh l&agrave; ai?</p>\r\n<p dir=\"ltr\">Thomas vỗ ngực tự h&agrave;o:</p>\r\n<p dir=\"ltr\">- Người 3 năm liền đoạt c&uacute;p v&ocirc; địch của cuộc thi n&oacute;i dối của th&agrave;nh phố.</p>\r\n<p dir=\"ltr\">- !?!</p>\r\n<p>&nbsp;</p>','2022-05-31 11:06:04.850000','p40df0d2f-5a99-4cc4-a066-196254b4586e.png','VUA NÓI DỐI ĐI CÂU CÁ',4);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (10,'<p dir=\"ltr\">&Ocirc;ng thầy đồ nọ vốn t&iacute;nh tham ăn. Bữa ấy c&oacute; người mời đi ăn cỗ, thầy mới cho một cậu học tr&ograve; nhỏ theo hầu. &ETH;ến k&ecirc;nh, thầy ngồi v&agrave;o m&acirc;m cỗ, bảo học tr&ograve; đứng b&ecirc;n cạnh. Tr&ocirc;ng thấy trong m&acirc;m cỗ c&ograve;n nhiều b&aacute;nh tr&aacute;i, bụng no nhưng thầy lại mong muốn bỏ t&uacute;i mấy chiếc. Lo lắng mọi người xung quanh nh&igrave;n thấy th&igrave; mất thể diện, thầy mới cầm b&aacute;nh thản nhi&ecirc;n đưa cho học tr&ograve;, bảo:</p>\r\n<p dir=\"ltr\">- N&agrave;y, con cầm lấy!</p>\r\n<p dir=\"ltr\">Vừa đưa, thầy vừa nh&aacute;y ra hiệu bảo cất đưa về cho thầy.</p>\r\n<p dir=\"ltr\">Cậu học tr&ograve; kh&ocirc;ng hiểu được c&aacute;i nh&aacute;y mắt th&acirc;m th&uacute;y của thầy, tưởng thầy cho thật, liền b&oacute;c ngay ra ăn.</p>\r\n<p dir=\"ltr\">Thầy nh&igrave;n thấy, giận lắm, nhưng giữa đ&ocirc;ng đủ người kh&aacute;c, kh&ocirc;ng d&aacute;m mắng. &ETH;ến l&uacute;c ra về, thầy c&ograve;n tiếc mấy c&aacute;i b&aacute;nh, mong muốn kiếm cớ để trả th&ugrave; học tr&ograve;. Khi hai thầy tr&ograve; vừa mới c&ugrave;ng đi ngang nhau, thầy b&egrave;n phản ứng mắng học tr&ograve;:</p>\r\n<p dir=\"ltr\">- M&agrave;y đồng vai phải lứa với tao hay sao m&agrave; d&aacute;m đi ngang h&agrave;ng?</p>\r\n<p dir=\"ltr\">Tr&ograve; lo lắng, vội v&agrave;ng đi nhanh l&ecirc;n trước. Thầy lại gắt:</p>\r\n<p dir=\"ltr\">- M&agrave;y l&agrave; bố tao hay sao m&agrave; d&aacute;m ti&ecirc;n phong tao?</p>\r\n<p dir=\"ltr\">Tr&ograve; tụt l&ugrave;i lại sau. Thầy lại qu&aacute;t:</p>\r\n<p dir=\"ltr\">- Tao c&oacute; phải l&agrave; thằng t&ugrave; đ&acirc;u m&agrave; m&agrave;y phải đi sau &aacute;p giải.</p>\r\n<p dir=\"ltr\">Tr&ograve; ngơ ng&aacute;c quay lại thưa:</p>\r\n<p dir=\"ltr\">- Bẩm bẩm, con đi thế n&agrave;o thầy cũng mắng, vậy xin thầy bảo cho con n&ecirc;n thế n&agrave;o cho phải ạ?</p>\r\n<p dir=\"ltr\">Thầy chẳng ngần ngại g&igrave; nữa, hầm hầm bảo:</p>\r\n<p dir=\"ltr\">- Thế b&aacute;nh tao đ&acirc;u&hellip;?</p>\r\n<p>&nbsp;</p>','2022-05-31 11:09:46.510000',NULL,'Bánh tao đâu',4);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (11,'<p dir=\"ltr\">C&oacute; một anh ch&agrave;ng ph&agrave;m ăn tục uống, hễ ngồi v&agrave;o m&acirc;m l&agrave; chỉ gắp lấy gắp để, chẳng ngẩng mặt nh&igrave;n ai, cũng chẳng muốn chuyện tr&ograve; g&igrave;. Một lần đi ăn cỗ ở nh&agrave; nọ, c&oacute; &ocirc;ng kh&aacute;ch thấy anh ta ăn uống lỗ m&atilde;ng qu&aacute;, b&egrave;n l&acirc;n la gợi chuyện. &Ocirc;ng kh&aacute;ch hỏi:</p>\r\n<p dir=\"ltr\">- Chẳng hay &ocirc;ng người ở đ&acirc;u ta?</p>\r\n<p dir=\"ltr\">Anh ch&agrave;ng đ&aacute;p: Đ&acirc;y. Rồi cắm c&uacute;i ăn.</p>\r\n<p dir=\"ltr\">- Thế &ocirc;ng được mấy c&ocirc;, mấy cậu rồi?</p>\r\n<p dir=\"ltr\">- Mỗi.</p>\r\n<p dir=\"ltr\">N&oacute;i xong, lại gắp lia gắp lịa.</p>\r\n<p dir=\"ltr\">&Ocirc;ng kh&aacute;ch hỏi tiếp:</p>\r\n<p dir=\"ltr\">- C&aacute;c cụ th&acirc;n sinh &ocirc;ng chắc c&ograve;n cả chứ?</p>\r\n<p dir=\"ltr\">Anh ch&agrave;ng vẫn kh&ocirc;ng ngẩng đầu l&ecirc;n, bảo:</p>\r\n<p dir=\"ltr\">- Tiệt!</p>\r\n<p>&nbsp;</p>','2022-05-31 11:10:29.134000','p67217828-fdbe-4380-a8c8-bcb32bf72b16.png','Tham ăn tục uống',4);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (12,'<p dir=\"ltr\">C&oacute; một anh nh&agrave; gi&agrave;u nhưng h&agrave; tiện, bữa cơm đến chả c&oacute; g&igrave; ngo&agrave;i một đĩa rau luộc. Đ&atilde; vậy, lại c&ograve;n l&agrave;m con c&aacute; gỗ treo lơ lửng giữa nh&agrave;, dặn c&aacute;c con mỗi l&uacute;c ăn cứ nh&igrave;n đấy v&agrave; ch&eacute;p miệng mấy c&aacute;i rồi v&agrave; cơm v&agrave;o miệng coi như cũng ăn cơm với c&aacute; rồi.</p>\r\n<p dir=\"ltr\">Những đứa lớn th&igrave; nhất loạt nghe theo bố, c&ograve;n đứa &uacute;t mới l&ecirc;n 5 tuổi h&aacute;u ăn, n&oacute; nh&igrave;n l&ecirc;n con c&aacute; ch&eacute;p miệng mấy c&aacute;i rồi mới v&agrave; cơm. Thằng anh gi&aacute;p n&oacute; tr&ocirc;ng thấy m&aacute;ch.</p>\r\n<p dir=\"ltr\">- Bố ơi, thằng n&agrave;y n&oacute; ăn tham lắm, ch&eacute;p miệng 4 c&aacute;i rồi mới v&agrave; cơm đấy ạ!</p>\r\n<p dir=\"ltr\">Anh nh&agrave; gi&agrave;u liền mắng:</p>\r\n<p dir=\"ltr\">- N&oacute; ăn mặn cho n&oacute; chết kh&aacute;t!</p>\r\n<p>&nbsp;</p>','2022-05-31 11:16:54.746000',NULL,'Cho nó chết khát',4);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (13,'<p dir=\"ltr\">Một anh thường khoe m&igrave;nh bắn giỏi, b&aacute;ch ph&aacute;t b&aacute;ch tr&uacute;ng. H&ocirc;m n&agrave;o mang s&uacute;ng đi săn, kh&ocirc;ng bắn được con g&igrave; th&igrave; anh ta gh&eacute; v&agrave;o chợ mua v&agrave;i con chim x&aacute;ch về, n&oacute;i l&agrave; bắn được. Năm ấy, c&oacute; con hổ dữ về l&agrave;ng bắt lợn. L&agrave;ng cử anh ta săn cho được, kh&ocirc;ng th&igrave; l&agrave;ng nguy to! Anh ta kh&ocirc;ng biết l&agrave;m thế n&agrave;o, đ&agrave;nh phải v&aacute;c s&uacute;ng v&agrave;o rừng, muốn ra sao th&igrave; ra!</p>\r\n<p dir=\"ltr\">V&agrave;o rừng, anh ta t&igrave;m một c&aacute;i hang đ&aacute; thật k&iacute;n, trốn v&agrave;o ấy. Hai h&ocirc;m sau, c&oacute; tin đưa về, n&oacute;i con hổ đ&atilde; bị bắn chết rồi. Nhưng chờ m&atilde;i kh&ocirc;ng thấy anh ta đ&acirc;u cả. Cả l&agrave;ng x&ocirc; nhau v&agrave;o rừng t&igrave;m. &ETH;ến chiều, t&igrave;m thấy anh ta ở trong hang, mọi người vui mừng n&oacute;i cho anh ta biết l&agrave; con hổ đ&atilde; bị người l&agrave;ng b&ecirc;n bắt chết. Anh ta tỏ &yacute; kh&ocirc;ng bằng l&ograve;ng, n&oacute;i:</p>\r\n<p dir=\"ltr\">- Hai h&ocirc;m nay, t&ocirc;i ở đ&acirc;y chờ n&oacute;i đi qua bắt sống đem về nộp l&agrave;ng, b&acirc;y giờ người ta bắn chết rồi, thật uổng c&ocirc;ng t&ocirc;i qu&aacute;.</p>\r\n<p>&nbsp;</p>','2022-05-31 11:21:12.775000','pdc870d32-8f8b-44ec-9c36-ca8cc196ed3d.png','Khoe tài',4);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (14,'<p dir=\"ltr\">Chủ nh&agrave; kia đ&atilde;i kh&aacute;ch rượu. Vừa nhấp m&ocirc;i, ai cũng nhắm mắt k&ecirc;u \"Chua qu&aacute;.\"</p>\r\n<p dir=\"ltr\">Một &ocirc;ng kh&aacute;ch n&oacute;i:</p>\r\n<p dir=\"ltr\">- T&ocirc;i c&oacute; c&aacute;ch l&agrave; cho rượu mất chua.</p>\r\n<p dir=\"ltr\">Chủ nh&agrave; liền hỏi:</p>\r\n<p dir=\"ltr\">- L&agrave;m thế n&agrave;o th&igrave; hết chua được?</p>\r\n<p dir=\"ltr\">&Ocirc;ng kh&aacute;ch b&agrave;y:</p>\r\n<p dir=\"ltr\">- Kiếm một tờ giấy, bưng miệng hũ lại, rồi &uacute;p sấp xuống.</p>\r\n<p dir=\"ltr\">Lấy ngải cứu đốt đ&iacute;t hũ bảy mồi, cứ để thế cho đến rạng ng&agrave;y mai l&agrave; hết chua ngay.</p>\r\n<p dir=\"ltr\">Chủ nh&agrave; n&oacute;i:</p>\r\n<p dir=\"ltr\">- Thế th&igrave; rượu chảy hết c&ograve;n g&igrave;?</p>\r\n<p dir=\"ltr\">&Ocirc;ng kh&aacute;ch n&oacute;i:</p>\r\n<p dir=\"ltr\">- Rượu chua để l&agrave;m qu&aacute;i g&igrave; m&agrave; c&ograve;n tiếc!</p>\r\n<p>&nbsp;</p>','2022-05-31 11:21:54.797000','p2c24cc84-1027-4da0-b221-e1bb4b9f6299.png','Rượu chua',4);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (15,'<p dir=\"ltr\">Một ng&agrave;y kia, con lừa của b&aacute;c n&ocirc;ng d&acirc;n sảy ch&acirc;n ng&atilde; xuống c&aacute;i giếng bỏ hoang. Con vật k&ecirc;u l&ecirc;n thảm thiết nhiều giờ liền trong l&uacute;c người chủ của n&oacute; nghĩ xem n&ecirc;n l&agrave;m g&igrave; để cứu con lừa l&ecirc;n&hellip; Cuối c&ugrave;ng, &ocirc;ng quyết định rằng, v&igrave; con lừa cũng gi&agrave; rồi v&agrave; c&aacute;i giếng th&igrave; đằng n&agrave;o cũng phải lấp, n&ecirc;n sẽ c&oacute; c&aacute;ch để kh&ocirc;ng phải bận t&acirc;m đến con lừa nữa.</p>\r\n<p dir=\"ltr\">&Ocirc;ng mời h&agrave;ng x&oacute;m đến gi&uacute;p &ocirc;ng một tay. Mỗi ngưởi cầm một c&aacute;i xẻng x&uacute;c đất đổ v&agrave;o giếng. Nhận ra sự thật phũ ph&agrave;ng, con lừa r&ecirc;n rỉ thảm thiết. Sau khi hứng những xẻng đất đầu ti&ecirc;n, n&oacute; ho&agrave;n to&agrave;n tuyệt vọng, nh&igrave;n l&ecirc;n với đ&ocirc;i mắt đầy ai &oacute;an. Chỉ đến khi đất ngập đến gần hết ch&acirc;n, n&oacute; mới bừng tỉnh, n&oacute; cảm nhận điều g&igrave; đ&oacute; đang v&agrave; sẽ xảy ra đối với n&oacute;. N&oacute; kh&ocirc;ng nh&igrave;n l&ecirc;n nữa m&agrave; cố gắng xoay sở để trồi l&ecirc;n. B&aacute;c n&ocirc;ng d&acirc;n v&agrave; mọi người chăm ch&uacute; nh&igrave;n xuống giếng, họ kinh ngạc trước những g&igrave; đang diễn ra. Cứ mỗi xẻng đất đổ l&ecirc;n lưng, con lừa lại lắc m&igrave;nh cho đất r&ocirc;i xuống ch&acirc;n v&agrave; bước l&ecirc;n lớp đất ấy. Cứ thế, từng xẻng đất, rồi từng lớp đất. V&agrave; chẳng bao l&acirc;u, ch&uacute; lừa đ&atilde; c&oacute; thể bước l&ecirc;n miệng giếng, mệt nhọc chạy ra ngo&agrave;i trước sự ngạc nhi&ecirc;n của tất cả mọi người.</p>\r\n<p dir=\"ltr\">C&acirc;u chuyện l&agrave; b&agrave;i học s&acirc;u sắc về c&aacute;ch th&iacute;ch ứng với ho&agrave;n cảnh sống khắc nghiệt, trong cuộc sống, ch&uacute;ng ta c&oacute; những l&uacute;c sẽ rơi v&agrave;o những nghịch cảnh v&ocirc; c&ugrave;ng kh&oacute; khăn, điều quan trọng kh&ocirc;ng phải ngồi v&agrave; than v&atilde;n, m&agrave; phải biết lợi dụng kh&oacute; khăn để biến n&oacute; th&agrave;nh cơ hội, kh&ocirc;ng được bỏ cuộc trong mọi ho&agrave;n cảnh.</p>\r\n<p>&nbsp;</p>','2022-05-31 11:24:55.961000','pa22d4171-f354-4ad6-b0b4-b6db86d6c576.jpg','Con lừa và bác nông dân',5);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (16,'<p dir=\"ltr\">Người l&aacute;i bu&ocirc;n phải thường xuy&ecirc;n v&agrave;o th&agrave;nh để mua đồ, v&agrave; chất l&ecirc;n vai Lừa để trở về nh&agrave;. Con Lừa của người l&aacute;i bu&ocirc;n nghĩ rằng m&igrave;nh rất th&ocirc;ng minh, l&uacute;c n&agrave;o cũng th&iacute;ch động n&atilde;o, nghĩ mọi c&aacute;ch để c&oacute; thể khiến cho đồ m&igrave;nh phải chở c&agrave;ng nhẹ c&agrave;ng tốt.&nbsp;</p>\r\n<p dir=\"ltr\">Một lần, người l&aacute;i bu&ocirc;n mua một tải muối. &Ocirc;ng ta buộc tải thật chặt, chất l&ecirc;n lưng Lừa, rồi đi đằng sau th&uacute;c Lừa đi nhanh. Đi được một l&uacute;c, người l&aacute;i bu&ocirc;n v&agrave; Lừa đi đến một con s&ocirc;ng nhỏ, người l&aacute;i bu&ocirc;n th&uacute;c Lừa lội xuống nước. Lừa kh&ocirc;ng cẩn thận đ&atilde; bị trượt ch&acirc;n, ng&atilde; xuống nước. Nước s&ocirc;ng đ&atilde; ngấm v&agrave;o miệng tải muối khiến cho muối bị tan ra rất nhiều.</p>\r\n<p dir=\"ltr\">Khi Lừa ta đứng dậy th&igrave; ph&aacute;t hiện ra tải muối m&agrave; m&igrave;nh chở đ&atilde; nhẹ hơn rất nhiều, trong l&ograve;ng cảm thấy th&iacute;ch ch&iacute; lắm. C&oacute; được kinh nghiệm lần n&agrave;y, mỗi lần qua s&ocirc;ng, Lừa ta đều giả vờ trượt ch&acirc;n ng&atilde; để cho bớt một &iacute;t đồ tr&ecirc;n lưng xuống s&ocirc;ng. Người l&aacute;i bu&ocirc;n biết vậy, định bụng sẽ phạt Lừa. Người l&aacute;i bu&ocirc;n v&agrave;o trong th&agrave;nh mua một tải b&ocirc;ng, v&agrave; chất l&ecirc;n lưng Lừa. Lại một lần nữa đi qua con s&ocirc;ng nhỏ, nh&igrave;n thấy s&ocirc;ng, Lừa ta vui mừng thầm reo l&ecirc;n, kh&ocirc;ng ngần ngừ bước xuống s&ocirc;ng. Khi đến giữa s&ocirc;ng, Lừa ta lại giả vờ trượt ch&acirc;n ng&atilde;. Lừa sung sướng nghĩ: \"Khi m&agrave; m&igrave;nh đứng l&ecirc;n chắc chắn đồ tr&ecirc;n lưng m&igrave;nh sẽ nhẹ hơn rất nhiều đ&acirc;y\".</p>\r\n<p dir=\"ltr\">Nhưng khi Lừa ta vừa mới đứng dậy, đột nhi&ecirc;n thấy đồ tr&ecirc;n lưng m&igrave;nh nặng hơn trước rất nhiều. Th&igrave; ra, tải b&ocirc;ng sau khi h&uacute;t nước đ&atilde; trở n&ecirc;n nặng hơn trước rất nhiều. Lừa ta kh&ocirc;ng ngờ tr&ecirc;n lưng tải b&ocirc;ng nặng như vậy, đ&agrave;nh c&uacute;i đầu ngậm ng&ugrave;i đi tiếp kh&ocirc;ng d&aacute;m than v&atilde;n g&igrave;.</p>\r\n<p dir=\"ltr\">Trong cuộc sống, th&agrave;nh quả lu&ocirc;n đi k&egrave;m với nỗ lực, nỗ lực bao nhi&ecirc;u th&igrave; th&agrave;nh quả đạt được bấy nhi&ecirc;u, v&igrave; vậy kh&ocirc;ng n&ecirc;n v&igrave; lười biếng m&agrave; gian tr&aacute;, kh&ocirc;ng cố gắng thực hiện đ&uacute;ng chức phận của m&igrave;nh. Lười biếng, tho&aacute;i th&aacute;c c&ocirc;ng việc sẽ phải nhận hậu quả th&iacute;ch đ&aacute;ng.&nbsp;</p>\r\n<p>&nbsp;</p>','2022-05-31 11:26:05.067000','p2dfa167b-e6f6-4972-8c17-07923d49cf8a.jpeg','Người lái buôn và con lừa',5);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (17,'<p dir=\"ltr\">B&ecirc;n bờ s&ocirc;ng c&oacute; một ch&uacute; R&ugrave;a đang ra sức tập bay.</p>\r\n<p dir=\"ltr\">&ndash; Cố l&ecirc;n n&agrave;o&hellip; 1, 2, 3&hellip; Cố l&ecirc;n&hellip;</p>\r\n<p dir=\"ltr\">Một con Chim Sẻ bay ngang qua, thấy thế liền hỏi:</p>\r\n<p dir=\"ltr\">&ndash; Anh R&ugrave;a ơi, anh đang l&agrave;m g&igrave; thế?</p>\r\n<p dir=\"ltr\">R&ugrave;a thở d&agrave;i đ&aacute;p:</p>\r\n<p dir=\"ltr\">&ndash; T&ocirc;i đang tập bay đấy, Chim Sẻ ạ.</p>\r\n<p dir=\"ltr\">Nghe vậy, Chim Sẻ rất ngạc nhi&ecirc;n, hỏi lại R&ugrave;a:</p>\r\n<p dir=\"ltr\">&ndash; Sao cơ? Chẳng phải anh đ&atilde; chiến thắng trong cuộc thi chạy với Thỏ đ&oacute; sao? Tất cả l&agrave; nhờ bốn chiếc ch&acirc;n của anh m&agrave;.</p>\r\n<p dir=\"ltr\">R&ugrave;a nhăn mặt trả lời:</p>\r\n<p dir=\"ltr\">&ndash; Th&ocirc;i th&ocirc;i, anh đừng nhắc nữa. T&ocirc;i v&agrave; Thỏ đ&atilde; thi lại lần nữa. Thỏ kh&ocirc;ng ngủ qu&ecirc;n giữa cuộc nữa n&ecirc;n đ&atilde; dễ d&agrave;ng thắng t&ocirc;i. Lần n&agrave;y, khi tập bay được, t&ocirc;i sẽ quyết đấu một trận nữa với Thỏ.</p>\r\n<p dir=\"ltr\">Chim Sẻ cười:</p>\r\n<p dir=\"ltr\">&ndash; Nhưng m&agrave; anh đ&acirc;u c&oacute; c&aacute;nh!</p>\r\n<p dir=\"ltr\">Nhưng R&ugrave;a vẫn kh&ocirc;ng lay chuyển.</p>\r\n<p dir=\"ltr\">&ndash; Bất kể thế n&agrave;o t&ocirc;i cũng phải học bay cho bằng được, Chim Sẻ ạ!</p>\r\n<p dir=\"ltr\">Chim Sẻ lại n&oacute;i:</p>\r\n<p dir=\"ltr\">&ndash; Nhưng anh đ&acirc;u c&oacute; c&aacute;nh th&igrave; l&agrave;m sao m&agrave; bay được, t&ocirc;i khuy&ecirc;n anh n&ecirc;n từ bỏ &yacute; định đ&oacute; đi th&igrave; hơn. Th&ocirc;i, t&ocirc;i đi chơi đ&acirc;y!</p>\r\n<p dir=\"ltr\">Chim Sẻ bỏ đi rồi, R&ugrave;a đi kiếm về rất nhiều l&ocirc;ng chim, may cho m&igrave;nh một đ&ocirc;i c&aacute;nh tuyệt đẹp. N&oacute; ra sức tập luyện, nhưng đ&atilde; mấy ng&agrave;y tr&ocirc;i qua m&agrave; vẫn kh&ocirc;ng c&oacute; g&igrave; tiến triển. R&ugrave;a nghĩ:</p>\r\n<p dir=\"ltr\">&ndash; Thế n&agrave;y kh&ocirc;ng ổn. M&igrave;nh phải đi mời thầy về dạy mới được.</p>\r\n<p dir=\"ltr\">Ng&agrave;y h&ocirc;m sau, R&ugrave;a l&ecirc;n đường đi t&igrave;m thầy dạy bay. R&ograve;ng r&atilde; mấy ng&agrave;y, n&oacute; đi đến một v&aacute;ch n&uacute;i cheo leo, hi vọng sẽ t&igrave;m được thầy. Một h&ocirc;m, R&ugrave;a đi tới một v&aacute;ch đ&aacute;, đột nhi&ecirc;n c&oacute; một đ&ocirc;i c&aacute;nh lớn liệng qua. R&ugrave;a ta v&ocirc; c&ugrave;ng ngưỡng mộ, nghĩ bụng:</p>\r\n<p dir=\"ltr\">&ndash; Đ&acirc;y ch&iacute;nh l&agrave; người thầy m&agrave; m&igrave;nh đang t&igrave;m kiếm.</p>\r\n<p dir=\"ltr\">R&ugrave;a liền h&eacute;t to:</p>\r\n<p dir=\"ltr\">&ndash; Đại B&agrave;ng ơi, xin h&atilde;y dạy t&ocirc;i bay với!</p>\r\n<p dir=\"ltr\">Đại B&agrave;ng &acirc;n cần nhắc nhở R&ugrave;a:</p>\r\n<p dir=\"ltr\">&ndash; T&ocirc;i v&agrave; R&ugrave;a kh&ocirc;ng giống nhau. R&ugrave;a kh&ocirc;ng c&oacute; c&aacute;nh, l&agrave;m sao m&agrave; bay được!</p>\r\n<p dir=\"ltr\">R&ugrave;a cầm ra đ&ocirc;i c&aacute;nh tự l&agrave;m, li&ecirc;n tục xin:</p>\r\n<p dir=\"ltr\">&ndash; Đại B&agrave;ng xem, t&ocirc;i c&oacute; c&aacute;nh rồi đ&acirc;y n&agrave;y. Xin anh h&atilde;y nhận t&ocirc;i l&agrave;m đồ đệ đi.</p>\r\n<p dir=\"ltr\">R&ugrave;a tự lắp th&ecirc;m đ&ocirc;i c&aacute;nh, Đại B&agrave;ng nhấc bổng R&ugrave;a l&ecirc;n, bay cao hơn những ngọn c&acirc;y. R&ugrave;a th&iacute;ch qu&aacute; reo l&ecirc;n:</p>\r\n<p dir=\"ltr\">&ndash; A ha! M&igrave;nh sắp biết bay rồi!</p>\r\n<p dir=\"ltr\">Đang bay tr&ecirc;n kh&ocirc;ng trung th&igrave; Đại B&agrave;ng bỏ R&ugrave;a ra. R&ugrave;a ta giống như diều đứt d&acirc;y, rơi tự do xuống, mặc cho R&ugrave;a cố gắng vỗ đ&ocirc;i c&aacute;nh tới tấp nhưng kh&ocirc;ng ăn thua g&igrave;.</p>\r\n<p dir=\"ltr\">&ndash; Cứu với! Ai cứu t&ocirc;i với&hellip;</p>\r\n<p dir=\"ltr\">R&ugrave;a rơi thẳng xuống một tảng đ&aacute; to, khiến cho mai R&ugrave;a bị vỡ rạn.</p>\r\n<p dir=\"ltr\">Kể từ đ&oacute;, tr&ecirc;n mai của R&ugrave;a c&oacute; những vết rạn ngang dọc, đ&oacute; l&agrave; dấu t&iacute;ch của lần R&ugrave;a học bay với Đại B&agrave;ng.</p>\r\n<p dir=\"ltr\">C&acirc;u chuyện khuy&ecirc;n ch&uacute;ng ta phải nhận thức đ&uacute;ng đắn về bản th&acirc;n m&igrave;nh, thay v&igrave; ước vọng những khả năng kh&ocirc;ng bao giờ thuộc về m&igrave;nh th&igrave; h&atilde;y cố gắng ph&aacute;t huy những ưu điểm của bản th&acirc;n m&igrave;nh.&nbsp;</p>\r\n<p>&nbsp;</p>','2022-05-31 11:27:23.234000','p7ba8af32-8645-4c58-a0db-a379d61fc347.jpeg','Con rùa học bay',5);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (18,'<p dir=\"ltr\">Xưa c&oacute; một con C&oacute;c l&agrave;m bạn với một con Chuột th&acirc;n lắm. Chuột thường vẫn xuống nh&agrave; C&oacute;c chơi lu&ocirc;n.</p>\r\n<p dir=\"ltr\">Sau C&oacute;c nghe n&oacute;i vợ Chuột mới nằm bếp, C&oacute;c nghĩ m&igrave;nh l&agrave; bạn, muốn đến mừng. Nhưng Chuột l&agrave;m tổ tr&ecirc;n c&acirc;y cao, C&oacute;c kh&ocirc;ng biết l&agrave;m sao l&ecirc;n được. Một h&ocirc;m, C&oacute;c gặp Chuột đang đi chợ, C&oacute;c n&oacute;i với Chuột rằng:</p>\r\n<p dir=\"ltr\">&ndash; T&ocirc;i nghe b&aacute;c mới ở cữ ch&aacute;u trai, lấy t&igrave;nh anh em, t&ocirc;i cũng muốn đến, trước l&agrave; thăm hai b&aacute;c, sau l&agrave; mừng cho ch&aacute;u. Song t&ocirc;i kh&ocirc;ng biết l&agrave;m thế n&agrave;o m&agrave; l&ecirc;n chơi được.</p>\r\n<p dir=\"ltr\">Chuột n&oacute;i:</p>\r\n<p dir=\"ltr\">&ndash; B&aacute;c c&oacute; l&ograve;ng l&ecirc;n chơi với thầy ch&aacute;u v&agrave; mừng cho ch&aacute;u, th&igrave; t&ocirc;i cũng c&oacute; c&aacute;ch đưa b&aacute;c l&ecirc;n dễ lắm.</p>\r\n<p dir=\"ltr\">C&oacute;c bảo:</p>\r\n<p dir=\"ltr\">&ndash; L&agrave;m thế n&agrave;o m&agrave; l&ecirc;n được?</p>\r\n<p dir=\"ltr\">Chuột n&oacute;i:</p>\r\n<p dir=\"ltr\">&ndash; T&ocirc;i c&oacute; c&aacute;i đu&ocirc;i d&agrave;i, b&aacute;c ngậm v&agrave;o c&aacute;i đu&ocirc;i ấy, t&ocirc;i l&ecirc;n được th&igrave; b&aacute;c cũng l&ecirc;n được.</p>\r\n<p dir=\"ltr\">C&oacute;c nghe n&oacute;i vui l&ograve;ng ngậm v&agrave;o đu&ocirc;i Chuột để Chuột k&eacute;o l&ecirc;n c&acirc;y. Đến l&uacute;c l&ecirc;n tới gần cửa, Chuột đực trong nh&agrave; chạy ra vồn v&atilde; hỏi:</p>\r\n<p dir=\"ltr\">&ndash; &Ocirc; k&igrave;a b&aacute;c C&oacute;c! L&acirc;u nay ch&uacute;ng t&ocirc;i vẫn mong b&aacute;c, mời b&aacute;c l&ecirc;n chơi.</p>\r\n<p dir=\"ltr\">C&oacute;c thấy n&oacute;i, mở miệng để đ&aacute;p lại, nhưng chưa kịp đ&aacute;p th&igrave; đ&atilde; rơi bịch xuống đất, xương sống g&atilde;y v&agrave; lưng cong lại.</p>\r\n<p dir=\"ltr\">Từ đ&oacute;, C&oacute;c c&oacute; bệnh đau lưng, hễ khi n&agrave;o giở trời, th&igrave; cứ ngồi m&agrave; nghiến răng k&egrave;n kẹt.</p>\r\n<p dir=\"ltr\">C&acirc;u chuyện ngụ ng&ocirc;n nhắc nhở ta phải biết cẩn thận, kh&ocirc;ng n&ecirc;n vội v&atilde; dẫn đến những hậu quả kh&ocirc;n lường. Ngo&agrave;i ra, truyện c&ograve;n giải th&iacute;ch một c&aacute;ch h&oacute;m hỉnh v&igrave; sao con C&oacute;c ng&agrave;y nay lưng bị cong lại v&agrave; nghiến răng k&egrave;n kẹt.</p>\r\n<p>&nbsp;</p>','2022-05-31 11:28:00.000000','p959e06ba-0489-473c-a558-3852c5264d7a.jpeg','Con cóc và con chuột',5);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (19,'<p dir=\"ltr\">Chị tung tăng đi khắp c&aacute;c ngả đường, mặc trời mưa hay nắng, d&aacute;ng chị thong dong b&ecirc;n d&ograve;ng đời vội v&atilde;. Đầu x&ugrave; t&oacute;c rối bộ quần &aacute;o tr&ocirc;ng kh&aacute; lập dị, miệng chị lu&ocirc;n lu&ocirc;n nở nụ cười, nụ cười ấy chưa bao giờ kh&eacute;p lại. Đ&aacute;m trẻ nhỏ chạy theo tr&ecirc;u chọc kh&ocirc;ng c&oacute; sự t&ocirc;n k&iacute;nh n&agrave;o d&agrave;nh cho chị. Xung quanh chẳng mấy ai để &yacute;, quan t&acirc;m đến người phụ nữ n&agrave;y.</p>\r\n<p dir=\"ltr\">Giữa c&aacute;i nắng như thi&ecirc;u đốt S&agrave;i G&ograve;n, nghe từng cơn gi&oacute; phả v&agrave;o da thịt bỏng r&aacute;t, nh&igrave;n xa xa con đường bốc hơi l&ecirc;n như kh&oacute;i, như nấu mặt đường oi bức cả một kh&ocirc;ng gian. Vẫn c&oacute; người phụ nữ đ&ocirc;i ch&acirc;n trần lang thang bước đi trong hồn nhi&ecirc;n, trong niềm vui v&agrave; hạnh ph&uacute;c của ri&ecirc;ng m&igrave;nh. Ph&iacute;a sau nụ cười ấy l&agrave; một l&agrave; cả một trời đau thương lẫn nước mắt. T&ocirc;i hiểu ra một sự thật của cuộc sống rằng: khi những nỗi buồn &acirc;u lo dồn dập đến, những con người mạnh mẽ sẽ đương đầu với n&oacute;, nhưng nếu n&oacute; đến qu&aacute; nhiều th&igrave; một người kh&ocirc;ng c&oacute; sức n&agrave;o để chống chọi nổi, l&uacute;c kiệt sức đ&agrave;nh phải bu&ocirc;ng xu&ocirc;i v&agrave; hứng chịu sự v&ugrave;i dập của n&oacute;, v&agrave; kết quả l&agrave; những cơn &aacute;c mộng, nỗi tự kỷ, tinh thần rơi v&agrave;o hoảng loạn, trầm cảm nặng v&agrave; cuối c&ugrave;ng điểm đến đ&iacute;ch của chứng bệnh t&acirc;m thần.</p>\r\n<p dir=\"ltr\">Thuở ấy, chị vẫn c&oacute; gia đ&igrave;nh như bao người phụ nữ người kh&aacute;c, cuộc sống hạnh ph&uacute;c vi&ecirc;n m&atilde;n trong căn nh&agrave; đầy ắp tiếng n&oacute;i cười. Chị đảm đang mọi việc v&agrave; giỏi trong chuyện mua b&aacute;n, nhờ v&agrave;o sự chịu kh&oacute;, t&iacute;nh to&aacute;n tỉ mỉ n&ecirc;n kh&ocirc;ng l&acirc;u sau đ&oacute; chị trở th&agrave;nh người gi&agrave;u. Ba mẹ chồng thương hết mực, họ h&atilde;nh diện về chị. Kh&aacute;c với c&ocirc; em chồng suốt ng&agrave;y chỉ biết nằm &igrave;, s&aacute;ng b&agrave;i tứ sắc, trưa ăn rồi nằm, tối th&igrave; k&egrave; cặp bạn b&egrave; đến vũ trường th&acirc;u đ&ecirc;m suốt s&aacute;ng, cứ thế hết ng&agrave;y n&agrave;y qua th&aacute;ng nọ. Hai mươi ba tuổi chưa c&oacute; mối n&agrave;o d&aacute;m rước. Ba mẹ chồng hay than phiền về c&ocirc; con g&aacute;i rượu của m&igrave;nh. Từ khi c&oacute; chị d&acirc;u th&igrave; lười biếng hẳn ra, c&aacute;i nh&agrave; kh&ocirc;ng qu&eacute;t, c&aacute;i ch&eacute;n kh&ocirc;ng rửa, chỉ biết ăn rồi đi chơi, việc c&ograve;n lại chị d&acirc;u l&agrave;m hết. Một h&ocirc;m b&agrave; n&oacute;i với con g&aacute;i ruột của m&igrave;nh.</p>\r\n<p dir=\"ltr\">- M&agrave;y coi l&agrave;m phụ với chị d&acirc;u Li&ecirc;n đi, c&aacute;i g&igrave; m&agrave;y cũng bắt n&oacute; l&agrave;m, n&oacute; về nh&agrave; m&igrave;nh l&agrave;m d&acirc;u chứ kh&ocirc;ng phải l&agrave;m đầy tớ đ&acirc;u con.</p>\r\n<p dir=\"ltr\">&Ocirc;ng cha đang ngồi tr&ecirc;n chiếc ghế xếp, tay cầm ly tr&agrave; n&oacute;ng đưa s&aacute;t l&ecirc;n m&ocirc;i thổi v&ugrave; v&ugrave; cũng n&oacute;i v&agrave;o.</p>\r\n<p dir=\"ltr\">- Lớn rồi con kh&ocirc;ng phải c&ograve;n nhỏ nữa, coi l&agrave;m tiếp chị b&acirc;y, một m&igrave;nh n&oacute; l&agrave;m kh&ocirc;ng xuể, việc nh&agrave;, chăm s&oacute;c con c&aacute;i, chuyện bu&ocirc;n b&aacute;n ngo&agrave;i cửa h&agrave;ng nữa. N&oacute; kh&ocirc;ng c&oacute; ph&uacute;t n&agrave;o quởn, c&ograve;n m&agrave;y th&igrave; chỉ biết đi chơi. Anh hai m&agrave;y th&igrave; tật nguyền n&ecirc;n kh&ocirc;ng l&agrave;m g&igrave; được việc, đ&aacute;ng l&yacute; ra m&agrave;y phải thay anh 2 l&agrave;m tiếp với n&oacute; mới phải.</p>\r\n<p dir=\"ltr\">Nghe xong c&ocirc; n&eacute;m c&aacute;i quạt cầm tay xuống nền gạch.</p>\r\n<p dir=\"ltr\">- Mệt ba m&aacute; qu&aacute;, l&agrave;m d&acirc;u th&igrave; phải phục vụ cho nh&agrave; chồng chứ, kh&ocirc;ng lẽ anh hai lấy vợ về cho chỉ ăn rồi đẻ. Rồi sau n&agrave;y lấy hết t&agrave;i sản, ở đ&acirc;u ra c&aacute;i chuyện n&agrave;y.</p>\r\n<p dir=\"ltr\">&Ocirc;ng hớp v&agrave;i ngụm tr&agrave; rồi nghi&ecirc;m giọng.</p>\r\n<p dir=\"ltr\">- M&agrave;y n&oacute;i vậy m&agrave; nghe được hả Phương, anh hai m&agrave;y t&agrave;n tật. N&oacute; lấy anh hai m&agrave;y l&agrave;m chồng l&agrave; phước đức ba đời rồi con, lại về đ&acirc;y l&agrave;m đủ thứ việc, m&agrave;y kh&ocirc;ng thấy n&oacute; khổ sao Phương.</p>\r\n<p dir=\"ltr\">B&agrave; lắc đầu quay sang nh&igrave;n chồng rồi n&oacute;i.</p>\r\n<p dir=\"ltr\">- T&ocirc;i với &ocirc;ng đừng n&oacute;i chuyện với n&oacute;. N&oacute; chọc t&ocirc;i với &ocirc;ng một hồi &oacute;i m&aacute;u chết b&acirc;y giờ, con với c&aacute;i.</p>\r\n<p dir=\"ltr\">Phương bắt đầu lớn tiếng.</p>\r\n<p dir=\"ltr\">- Sẵn đ&acirc;y ba m&aacute; n&oacute;i, giờ t&ocirc;i n&oacute;i lu&ocirc;n những c&aacute;i m&agrave; t&ocirc;i chịu đựng từ bấy l&acirc;u nay. T&ocirc;i l&agrave; con của ba m&aacute; m&agrave; chưa bao giờ ba m&aacute; d&agrave;nh t&igrave;nh thương cho t&ocirc;i cả, c&aacute;i g&igrave; tốt đẹp cũng con Li&ecirc;n, c&ograve;n những c&aacute;i g&igrave; xấu đều của con Phương n&agrave;y. Ba m&aacute; nghĩ lại xem trước kia chị Li&ecirc;n chưa về th&igrave; mọi việc trong nh&agrave; ai l&agrave;m, ai lo cho ba m&aacute; với anh hai, c&oacute; phải một tay con Phương n&agrave;y kh&ocirc;ng, giờ về rồi th&igrave; l&agrave;m lại để t&ocirc;i nghỉ chứ, t&agrave;i sản sau n&agrave;y thuộc về bả m&agrave;.</p>\r\n<p dir=\"ltr\">&Ocirc;ng đập tay xuống b&agrave;n, chỉ thẳng tay v&agrave;o mặt Phương.</p>\r\n<p dir=\"ltr\">- M&agrave;y im miệng ngay. Những lời mất dạy đ&oacute; m&agrave; n&oacute;i ra được sao. Tao với bả c&ograve;n sống sờ sờ đ&acirc;y m&agrave; m&agrave;y n&oacute;i đến chuyện t&agrave;i sản. Sao m&agrave;y kh&ocirc;ng giết tao với bả chết đi rồi lấy t&agrave;i sản đi ăn chơi.</p>\r\n<p dir=\"ltr\">B&agrave; quay sang Phương rồi nhẹ giọng.</p>\r\n<p dir=\"ltr\">- Th&ocirc;i m&agrave;y im đi, ổng đang n&oacute;ng, đừng lớn tiếng người ta cười nh&agrave; m&igrave;nh, th&ocirc;i &ocirc;ng cũng im đi.</p>\r\n<p dir=\"ltr\">&Ocirc;ng nạt lớn.</p>\r\n<p dir=\"ltr\">- Đ&oacute;. B&agrave; thấy đ&oacute;, nu&ocirc;i n&oacute; từ đ&oacute; tới giờ, giờ n&oacute; n&oacute;i ra những c&acirc;u đ&oacute; nghe được kh&ocirc;ng, chiều chuộng n&oacute; cho nhiều v&agrave;o.</p>\r\n<p dir=\"ltr\">Phương ấm ức kh&oacute;c.</p>\r\n<p dir=\"ltr\">- T&ocirc;i c&oacute; n&oacute;i sai đ&acirc;u m&agrave; &ocirc;ng b&agrave; cứ b&ecirc;nh cho người ngo&agrave;i rồi la. Anh 2 bị bệnh sống nay chết mai, ba m&aacute; gi&agrave; cũng sẽ chết, t&ocirc;i th&igrave; đi lấy chồng, thế th&igrave; t&agrave;i sản n&agrave;y thuộc về ai. Ba m&aacute; trả lời xem.</p>\r\n<p dir=\"ltr\">Nghe thấy nh&agrave; tr&ecirc;n c&oacute; chuyện c&atilde;i nhau, anh hai của Phương tr&ecirc;n chiếc xe lăn di chuyển l&ecirc;n nh&agrave; tr&ecirc;n đầy kh&oacute; khăn. Anh bị bại n&atilde;o phải ngồi xe lăn từ b&eacute; đến đến lớn, v&agrave; đủ thứ c&aacute;c chứng bệnh trong người, n&oacute; h&agrave;nh hạ anh đến gầy guộc teo h&eacute;o như một đứa trẻ, b&aacute;c sĩ chuẩn đo&aacute;n anh kh&ocirc;ng sống được l&acirc;u v&igrave; cơ địa yếu từ nhỏ, kh&ocirc;ng thể đẩy l&ugrave;i c&aacute;c chứng bệnh nan y trong người. Anh may mắn c&oacute; được một người vợ, anh xem đ&oacute; l&agrave; hạnh ph&uacute;c lớn nhất của cuộc đời m&igrave;nh như trời lấy đi c&aacute;i n&agrave;y, cho lại c&aacute;i kh&aacute;c vậy. Anh rất thương vợ, anh biết vợ m&igrave;nh về l&agrave;m d&acirc;u l&agrave; chịu rất nhiều thiệt th&ograve;i, nhiều khi anh thấy những người đ&agrave;n &ocirc;ng d&igrave;u dắt vợ con đi chơi m&agrave; anh buồn đến ph&aacute;t kh&oacute;c, bản th&acirc;n anh kh&ocirc;ng l&agrave;m được như họ, n&ecirc;n t&igrave;nh y&ecirc;u thương anh d&agrave;nh cho vợ m&igrave;nh nhiều hơn cả bản th&acirc;n m&igrave;nh. Anh di chuyển s&aacute;t đến nơi em g&aacute;i ngồi, đ&ocirc;i b&agrave;n tay b&eacute; nhỏ cố gồng hết sức m&igrave;nh, anh t&aacute;t thật mạnh v&agrave;o mặt em g&aacute;i muốn nh&aacute; lửa, đ&oacute; cũng l&agrave; lần đầu ti&ecirc;n trong cuộc đời, anh đ&aacute;nh em g&aacute;i ruột của m&igrave;nh. Chỉ tay v&agrave;o mặt Phương với giọng n&oacute;i đầy tức tối.</p>\r\n<p dir=\"ltr\">- Mất dạy.</p>\r\n<p dir=\"ltr\">Sau c&aacute;i t&aacute;t đau điếng của anh hai, Phương &ocirc;m mặt kh&oacute;c chạy v&agrave;o ph&ograve;ng, anh thở dồn dập v&igrave; c&aacute;i tức c&ograve;n đang trong người, hai &ocirc;ng b&agrave; tới khuy&ecirc;n sợ anh c&oacute; chuyện xảy ra khi đang mang bệnh trong người. B&agrave; b&oacute;p tay rồi vuốt ngực cho anh n&oacute;i những lời nhẹ nh&agrave;ng.</p>\r\n<p dir=\"ltr\">- B&igrave;nh tĩnh đi con. Phương n&oacute; n&oacute;i vậy chứ kh&ocirc;ng c&oacute; &yacute; g&igrave; đ&acirc;u.</p>\r\n<p dir=\"ltr\">&Ocirc;ng cũng tới vỗ d&agrave;nh anh.</p>\r\n<p dir=\"ltr\">- Con đang bệnh, đừng tức giận, cũng do c&aacute;i con nghiệp chướng kia m&agrave; ra, &ocirc;ng trời ơi t&ocirc;i c&oacute; l&agrave;m &aacute;c g&igrave; m&agrave; đối xử với t&ocirc;i như vậy.</p>\r\n<p dir=\"ltr\">Mắt anh nh&igrave;n chằm chằm ra cửa, n&oacute;i những lời uất nghẹn.</p>\r\n<p dir=\"ltr\">- Con kh&ocirc;ng cần t&agrave;i sản của c&aacute;i nh&agrave; n&agrave;y, c&aacute;i con cần l&agrave; Li&ecirc;n.</p>\r\n<p dir=\"ltr\">Anh di chuyển xa ph&iacute;a trước nh&agrave;, ngồi đ&oacute; nh&igrave;n trầm ng&acirc;m, may l&agrave; vợ anh đang ở cửa h&agrave;ng, nếu vợ anh c&oacute; ở nh&agrave; th&igrave; chắc chắn sẽ bị tổn thương bởi những lời kh&oacute; nghe của em ruột m&igrave;nh.</p>\r\n<p dir=\"ltr\">Ả nằm trong ph&ograve;ng kh&oacute;c &ugrave; oa. Ả suy nghĩ đủ thứ chuyện, một người anh thương m&igrave;nh nhất lại h&ocirc;m nay đ&aacute;nh m&igrave;nh chỉ v&igrave; Li&ecirc;n. Từ khi Li&ecirc;n về đ&acirc;y c&aacute;i g&igrave; cũng hơn m&igrave;nh, ba mẹ thương c&ocirc; ấy nhất, lu&ocirc;n so s&aacute;nh m&igrave;nh với chị d&acirc;u, v&agrave; t&agrave;i sản của nh&agrave; m&igrave;nh sau n&agrave;y sẽ thuộc về Li&ecirc;n, nếu kh&ocirc;ng c&oacute; Li&ecirc;n trong căn nh&agrave; n&agrave;y sẽ kh&ocirc;ng xảy ra chuyện như h&ocirc;m nay, anh em mất h&ograve;a thuận, ba mẹ th&igrave; kh&ocirc;ng nương chiều ả như l&uacute;c trước. Ả nghĩ đến chuyện phải thủ ti&ecirc;u chị d&acirc;u, nghĩ đi nghĩ lại, ả thấy kh&ocirc;ng được, v&igrave; kh&ocirc;ng trước sau g&igrave; ả cũng d&iacute;nh v&agrave;o v&ograve;ng lao l&yacute; với tội danh giết người, ả b&egrave;n suy nghĩ đến c&aacute;ch kh&aacute;c, c&aacute;ch n&agrave;o m&agrave; l&agrave;m cho Li&ecirc;n rời khỏi căn nh&agrave; n&agrave;y kh&ocirc;ng trở lại m&agrave; kh&ocirc;ng phải đi t&ugrave;. Cuối c&ugrave;ng ả cũng nghĩ ra được một c&aacute;ch rất đ&ecirc; h&egrave;n, đ&oacute; l&agrave; : vu khống cho Li&ecirc;n.</p>\r\n<p dir=\"ltr\">Ng&agrave;y mốt l&agrave; đ&aacute;m giỗ nội. Ả thừa biết năm n&agrave;o ba mẹ cũng mời đầy đủ họ h&agrave;ng v&agrave; c&oacute; cả cha mẹ ruột của Li&ecirc;n đến dự. Năm nay chắc chắn sẽ giống như những năm trước. Ả sẽ l&agrave;m nhục Li&ecirc;n trước mặt mọi người, nghĩ tới mưu kế n&agrave;y ả cười ha ha tự cho rằng m&igrave;nh th&ocirc;ng minh, những c&aacute;i suy nghĩ n&oacute; hiện trong đ&ocirc;i mắt đầy gian manh của ả. Ả cười thầm gật đầu rồi n&oacute;i một m&igrave;nh.</p>\r\n<p dir=\"ltr\">- Được... được... để coi ở đ&acirc;y được bao l&acirc;u, t&agrave;i sản của nh&agrave; n&agrave;y kh&ocirc;ng phải dễ lấy đ&acirc;u. Cứ chờ đ&oacute;.</p>\r\n<p dir=\"ltr\">Từ đ&oacute; Ả giả vờ ăn năn hối lỗi, xin lỗi ba mẹ v&agrave; cả anh hai. Mới đ&oacute; m&agrave; ả trở th&agrave;nh c&ocirc; g&aacute;i hiền ngoan, ả nấu cơm, dọn qu&eacute;t, rất si&ecirc;ng năng, kh&ocirc;ng c&ograve;n đi chơi nữa, mọi người ai cũng thấy l&agrave;m lạ. Cả nh&agrave; đều vui khi thấy ả thay đổi một c&aacute;ch lạ thường. Một buổi chiều ả k&ecirc;u anh hai ra n&oacute;i nhỏ, trước khi chở đi bệnh viện để c&aacute;c b&aacute;c sĩ theo d&otilde;i.</p>\r\n<p dir=\"ltr\">- Anh hai c&oacute; biết, tại sao em lại trở th&agrave;nh như vậy kh&ocirc;ng?</p>\r\n<p dir=\"ltr\">- Anh kh&ocirc;ng biết, nhưng anh vui lắm, v&igrave; đ&acirc;y mới l&agrave; em của anh như ng&agrave;y n&agrave;o. M&agrave; sao em trở th&agrave;nh như vậy.</p>\r\n<p dir=\"ltr\">- V&igrave; em muốn giữ t&agrave;i sản của m&igrave;nh, kh&ocirc;ng lọt v&agrave;o tay của người lạ.</p>\r\n<p dir=\"ltr\">- Em n&oacute;i g&igrave; vậy Phương. Anh l&agrave; anh của em kia m&agrave;, đ&acirc;u phải người lạ.</p>\r\n<p dir=\"ltr\">- Kh&ocirc;ng phải anh m&agrave; l&agrave; người kh&aacute;c.</p>\r\n<p dir=\"ltr\">- &Yacute; em n&oacute;i chị d&acirc;u của em sao.</p>\r\n<p dir=\"ltr\">- Nếu chị d&acirc;u hưởng được th&igrave; em mừng, v&igrave; đ&oacute; l&agrave; chị của em m&agrave;, nhưng kh&ocirc;ng phải, l&agrave; người kh&aacute;c.</p>\r\n<p dir=\"ltr\">- L&agrave; ai.</p>\r\n<p dir=\"ltr\">- L&agrave; bồ của chị d&acirc;u, chị d&acirc;u đang ngoại t&igrave;nh. Em chỉ sợ t&agrave;i sản n&agrave;y v&agrave;o tay của người đ&agrave;n &ocirc;ng lạ đ&oacute;.</p>\r\n<p dir=\"ltr\">- Em n&oacute;i c&aacute;i g&igrave; vậy Phương.</p>\r\n<p dir=\"ltr\">- Trời ơi, anh nhỏ tiếng th&ocirc;i, ba mẹ nghe b&acirc;y giờ. Ba mẹ l&uacute;c n&agrave;o cũng b&ecirc;nh vực chị d&acirc;u, em n&oacute;i chắc chắn ba mẹ kh&ocirc;ng tin đ&acirc;u, c&aacute;i n&agrave;y em chỉ n&oacute;i cho anh biết, anh đừng n&oacute;i lại với bất cứ ai v&igrave; em kh&ocirc;ng muốn nh&agrave; m&igrave;nh mang tiếng.</p>\r\n<p dir=\"ltr\">- Sao em lại biết Li&ecirc;n ngoại t&igrave;nh.</p>\r\n<p dir=\"ltr\">- Anh nghĩ đi, anh t&agrave;n tật thế n&agrave;y th&igrave; ai m&agrave; chịu lấy l&agrave;m chồng, huống g&igrave; chị d&acirc;u đi suốt đến tối mới về.</p>\r\n<p dir=\"ltr\">- Chỉ c&oacute; vậy m&agrave; em nghĩ chị d&acirc;u em ngoại t&igrave;nh hả Phương. Em c&oacute; biết Li&ecirc;n bận v&igrave; bu&ocirc;n b&aacute;n ở ngo&agrave;i chợ kh&ocirc;ng.</p>\r\n<p dir=\"ltr\">- Kh&ocirc;ng ch&iacute;nh mắt em thấy. Đ&ecirc;m đ&oacute; anh c&ograve;n nằm viện chưa về, em đang ngủ th&igrave; nghe tiếng ch&oacute; sủa ầm trời, bước ra th&igrave; thấy chị Li&ecirc;n ngo&agrave;i ng&otilde; n&oacute;i chuyện với ai đ&oacute;, m&agrave; họ rất l&eacute;n l&uacute;t, một l&aacute;t sau chị Li&ecirc;n dẫn người đ&agrave;n &ocirc;ng đ&oacute; v&agrave;o nh&agrave; rồi kh&eacute;p nhẹ c&aacute;nh cửa, bước v&agrave;o ph&ograve;ng anh. Em l&eacute;n coi th&igrave; r&otilde; r&agrave;ng chị Li&ecirc;n đang ngoại t&igrave;nh, họ kh&ocirc;ng mặc đồ, em la l&ecirc;n th&igrave; người đ&agrave;n &ocirc;ng đ&oacute; chạy tho&aacute;t v&igrave; c&aacute;nh cửa chỉ kh&eacute;p hờ kh&ocirc;ng c&oacute; kh&oacute;a.</p>\r\n<p dir=\"ltr\">Anh ngồi tr&ecirc;n xe lăn m&agrave; như chết lặng, hỏi lại để x&aacute;c minh.</p>\r\n<p dir=\"ltr\">- C&oacute; thật l&agrave; như vậy kh&ocirc;ng. Li&ecirc;n kh&ocirc;ng phải l&agrave; người như em n&oacute;i đ&acirc;u.</p>\r\n<p dir=\"ltr\">- Anh kh&ocirc;ng tin th&igrave; th&ocirc;i, vậy em kh&ocirc;ng n&oacute;i nữa. T&ugrave;y anh th&ocirc;i, t&agrave;i sản n&agrave;y l&agrave; của anh, em n&oacute;i vậy chỉ muốn giữ cho anh, chứ em kh&ocirc;ng c&oacute; g&igrave; trong n&agrave;y cả, em đi lấy chồng th&igrave; r&otilde; r&agrave;ng t&agrave;i sản n&agrave;y đ&acirc;u phải l&agrave; của em. Th&ocirc;i th&igrave; t&ugrave;y anh hai vậy, ra xe đi em chở anh l&ecirc;n bệnh viện rồi mai đ&oacute;n anh về.</p>\r\n<p dir=\"ltr\">Nằm trong bệnh viện anh nghĩ đến lời n&oacute;i của em g&aacute;i m&igrave;nh l&uacute;c chiều, nếu n&oacute; đ&uacute;ng l&agrave; sự thật th&igrave; quả l&agrave; một c&uacute; sốc lớn nhất trong cuộc đời của anh, v&igrave; t&igrave;nh cảm anh d&agrave;nh cho Li&ecirc;n lu&ocirc;n hết m&igrave;nh. Nhưng anh cũng nghi ngờ đứa em g&aacute;i của m&igrave;nh. Từ l&uacute;c Li&ecirc;n về l&agrave;m d&acirc;u, em g&aacute;i h&igrave;nh như rất gh&eacute;t, biết đ&acirc;u đ&acirc;y chỉ l&agrave; lời chia rẽ t&igrave;nh cảm của hai vợ chồng. Anh cứ nằm thao thức trong bệnh viện đầy hiu quạnh. L&uacute;c n&agrave;o anh nằm viện cũng c&oacute; Li&ecirc;n b&ecirc;n cạnh ngủ lại, nhưng giờ phải để cho Li&ecirc;n ở nh&agrave; v&igrave; mai l&agrave; đ&aacute;m giỗ, kh&ocirc;ng c&oacute; Li&ecirc;n ở nh&agrave; th&igrave; ai lo việc bếp n&uacute;c, đứa em g&aacute;i tuy l&agrave; phận đ&agrave;n b&agrave; nhưng lại qu&aacute; lười biếng. Nghĩ m&agrave; thương vợ v&ocirc; c&ugrave;ng, anh gạt đi lời n&oacute;i của em g&aacute;i m&igrave;nh sang một b&ecirc;n, xem đ&oacute; như lời chia rẽ, anh rất tin tưởng v&agrave;o Li&ecirc;n v&igrave; đ&atilde; chung sống bao nhi&ecirc;u năm nay n&ecirc;n anh hiểu vợ anh l&agrave; người thế n&agrave;o.</p>\r\n<p dir=\"ltr\">Ng&agrave;y giỗ nội năm nay cũng như năm trước. Ả biết h&ocirc;m nay Li&ecirc;n nghỉ b&aacute;n ở nh&agrave; nấu nướng như mọi năm, Li&ecirc;n dậy rất sớm lo l&agrave;m ở dưới bếp cho đến trời s&aacute;ng hẳn. Buổi s&aacute;ng họ h&agrave;ng c&oacute; mặt đầy đủ, c&oacute; cả ba mẹ ruột của Li&ecirc;n cũng đến ăn giỗ nh&agrave; sui gia như mọi năm. Họ đang ngồi uống tr&agrave; tr&ograve; chuyện, bỗng ở đ&acirc;u xuất hiện một g&atilde; đ&agrave;n &ocirc;ng mặc quần đ&ugrave;i, ở trần, từ trong ph&ograve;ng Li&ecirc;n chạy ra, tr&ecirc;n tay c&ograve;n cầm chiếc quần d&agrave;i v&agrave; c&aacute;i &aacute;o, gương mặt g&atilde; như sợ h&atilde;i. G&atilde; nhanh ch&oacute;ng chạy ra khỏi nh&agrave; với tiếng gọi của cha ả.</p>\r\n<p dir=\"ltr\">- Ai đ&oacute;... ai đ&oacute;...</p>\r\n<p dir=\"ltr\">Họ nh&igrave;n nhau kh&ocirc;ng biết người đ&oacute; l&agrave; ai, Mẹ ả quay sang hỏi cha ả.</p>\r\n<p dir=\"ltr\">- Ủa thằng đ&oacute; l&agrave; ai vậy, sao n&oacute; c&oacute; mặt trong ph&ograve;ng của con d&acirc;u m&igrave;nh.</p>\r\n<p dir=\"ltr\">Cha của Li&ecirc;n ph&acirc;n v&acirc;n.</p>\r\n<p dir=\"ltr\">- C&oacute; khi n&agrave;o con ch&aacute;u của &ocirc;ng b&agrave; sui ở xa về ăn giỗ kh&ocirc;ng.</p>\r\n<p dir=\"ltr\">Mẹ của ả n&oacute;i để khẳng định.</p>\r\n<p dir=\"ltr\">- L&agrave;m g&igrave; c&oacute; chuyện đ&oacute;, con ch&aacute;u của nh&agrave; n&agrave;y th&igrave; phải biết mặt chứ, nếu con ch&aacute;u của nh&agrave; n&agrave;y th&igrave; tại sao phải l&eacute;n la l&eacute;n l&uacute;t như vậy.</p>\r\n<p dir=\"ltr\">Mẹ của Li&ecirc;n im lặng một hồi, giờ mới l&ecirc;n tiếng.</p>\r\n<p dir=\"ltr\">- C&oacute; phải ăn trộm kh&ocirc;ng b&agrave; sui, xem lại nh&agrave; c&oacute; mất c&aacute;i g&igrave; kh&ocirc;ng.</p>\r\n<p dir=\"ltr\">Ba của ả n&oacute;i để khẳng định đ&oacute; l&agrave; điều sai.</p>\r\n<p dir=\"ltr\">- C&agrave;ng kh&ocirc;ng thể n&agrave;o l&agrave; ăn trộm, nếu ăn trộm th&igrave; n&oacute; phải giấu mặt chứ, đằng n&agrave;y thấy r&otilde; mặt n&oacute;, với ăn trộm n&agrave;o m&agrave; cởi trần, mặc quần đ&ugrave;i như vừa mới ngủ dậy.</p>\r\n<p dir=\"ltr\">Ba mẹ của Li&ecirc;n chỉ biết im lặng nh&igrave;n nhau m&agrave; kh&ocirc;ng hiểu chuyện g&igrave;. Mẹ của ả n&oacute;i nhỏ v&agrave;o tai chồng.</p>\r\n<p dir=\"ltr\">- &Ocirc;ng ơi, c&oacute; khi n&agrave;o lời Phương n&oacute;i với m&igrave;nh hồi chiều h&ocirc;m qua l&agrave; đ&uacute;ng sự thật kh&ocirc;ng.</p>\r\n<p dir=\"ltr\">- Chắc chắn l&agrave; đ&uacute;ng như lời Phương n&oacute;i rồi. Vậy m&agrave; từ bấy l&acirc;u nay t&ocirc;i cứ tưởng con Li&ecirc;n c&ocirc;ng dung ng&ocirc;n hạnh, một l&ograve;ng v&igrave; chồng, giờ t&ocirc;i mới biết m&igrave;nh đ&atilde; nh&igrave;n sai người.</p>\r\n<p dir=\"ltr\">Ba mẹ của Li&ecirc;n đứng như trời trồng, kh&ocirc;ng biết n&oacute;i như thế n&agrave;o. Cuối c&ugrave;ng mẹ của Li&ecirc;n cũng l&ecirc;n tiếng.</p>\r\n<p dir=\"ltr\">- Anh sui, chị sui, n&oacute;i g&igrave; t&ocirc;i kh&ocirc;ng hiểu. Con &Aacute;i Li&ecirc;n nh&agrave; t&ocirc;i n&oacute; kh&ocirc;ng l&agrave;m vậy đ&acirc;u, đừng n&oacute;i vậy tội nghiệp cho n&oacute;.</p>\r\n<p dir=\"ltr\">Mẹ của ả lắc đầu rồi đ&aacute;p.</p>\r\n<p dir=\"ltr\">- Anh chị sui kh&ocirc;ng hiểu th&igrave; k&ecirc;u con g&aacute;i của m&igrave;nh l&ecirc;n hỏi l&agrave; sẽ hiểu. Phụ nữ c&oacute; chồng m&agrave; c&ograve;n dẫn trai v&agrave;o nh&agrave; trong l&uacute;c chồng vắng nh&agrave;, thiệt l&agrave; xấu hổ. Đ&uacute;ng l&agrave; con trai nh&agrave; t&ocirc;i n&oacute; v&ocirc; phước mới cưới con g&aacute;i anh chị l&agrave;m vợ.</p>\r\n<p dir=\"ltr\">Mẹ của Li&ecirc;n chảy hai h&agrave;ng nước mắt. B&agrave; qu&aacute; xấu hổ khi con m&igrave;nh bị x&uacute;c phạm trước đ&aacute;m đ&ocirc;ng. Ba của Li&ecirc;n tức giận gọi to.</p>\r\n<p dir=\"ltr\">- Li&ecirc;n đ&acirc;u, m&agrave;y l&ecirc;n đ&acirc;y tao biểu.</p>\r\n<p dir=\"ltr\">Li&ecirc;n nghe ba của m&igrave;nh gọi, chị bỏ hết c&ocirc;ng việc dưới bếp vội v&agrave;ng chạy l&ecirc;n.</p>\r\n<p dir=\"ltr\">- Thưa cha gọi con.</p>\r\n<p dir=\"ltr\">- M&agrave;y c&ograve;n d&aacute;m k&ecirc;u tao l&agrave; cha nữa hay sao.</p>\r\n<p dir=\"ltr\">- Cha n&oacute;i g&igrave; con kh&ocirc;ng hiểu.</p>\r\n<p dir=\"ltr\">- Tại sao m&agrave;y lại d&aacute;m dẫn trai v&agrave;o nh&agrave;, khi chồng m&agrave;y kh&ocirc;ng c&oacute; ở nh&agrave;.</p>\r\n<p dir=\"ltr\">- Cha n&oacute;i c&aacute;i g&igrave; vậy, con c&oacute; dẫn ai v&agrave;o nh&agrave; đ&acirc;u.</p>\r\n<p dir=\"ltr\">Ả từ dưới bước l&ecirc;n, hai tay chắp sau lưng n&oacute;i giọng đầy mỉa mai.</p>\r\n<p dir=\"ltr\">- Chị đ&oacute;ng kịch giỏi thật, t&ocirc;i đ&atilde; thấy rất nhiều lần nhưng kh&ocirc;ng c&oacute; bằng chứng, giờ th&igrave; kh&ocirc;ng c&ograve;n g&igrave; để chối nữa.</p>\r\n<p dir=\"ltr\">- Phương em đang n&oacute;i g&igrave; vậy, chị c&oacute; dẫn ai v&agrave;o nh&agrave; đ&acirc;u.</p>\r\n<p dir=\"ltr\">- C&acirc;m miệng đi thứ ngoại t&igrave;nh, chị đ&atilde; lừa dối anh t&ocirc;i suốt mấy th&aacute;ng qua. Kh&ocirc;ng dẫn ai v&agrave;o nh&agrave;, m&agrave; ở đ&acirc;y ai cũng nh&igrave;n thấy.</p>\r\n<p dir=\"ltr\">Li&ecirc;n kh&oacute;c th&agrave;nh tiếng, nắm tay ba mẹ ruột rồi đến ba mẹ chồng.</p>\r\n<p dir=\"ltr\">- Ba mẹ ơi, con kh&ocirc;ng phải l&agrave; người như vậy, xin ba mẹ hiểu cho con. Con kh&ocirc;ng c&oacute; l&agrave;m chuyện đ&oacute;.</p>\r\n<p dir=\"ltr\">Ba của Li&ecirc;n, t&aacute;t chị thật mạnh như trời gi&aacute;ng.</p>\r\n<p dir=\"ltr\">- M&agrave;y đ&uacute;ng l&agrave; thứ lăng lo&agrave;n, tao kh&ocirc;ng c&oacute; đứa con như m&agrave;y, từ nay về sau m&agrave;y sống hay chết đ&oacute; l&agrave; chuyện của m&agrave;y, kh&ocirc;ng li&ecirc;n quan đến họ Dương nh&agrave; tao. Kể từ nay &acirc;n đoạn nghĩa tuyệt.</p>\r\n<p dir=\"ltr\">&Ocirc;ng quay sang c&uacute;i đầu trước ba mẹ của ả.</p>\r\n<p dir=\"ltr\">- T&ocirc;i xin lỗi hai &ocirc;ng b&agrave;, v&igrave; đ&atilde; kh&ocirc;ng dạy dỗ n&oacute; cho tốt. T&ocirc;i giao n&oacute; lại cho hai &ocirc;ng b&agrave;, muốn xử l&yacute; thế n&agrave;o t&ugrave;y &ocirc;ng b&agrave;. T&ocirc;i kh&ocirc;ng c&ograve;n mặt mũi n&agrave;o ở đ&acirc;y. T&ocirc;i về đ&acirc;y.</p>\r\n<p dir=\"ltr\">&Ocirc;ng v&agrave; vợ bước ch&acirc;n ra về với gương mặt đầy tức giận. Li&ecirc;n &ocirc;m mặt kh&oacute;c, tiếng kh&oacute;c như uất nghẹn ở cổ họng kh&ocirc;ng c&ograve;n ph&aacute;t ra tiếng chỉ c&ograve;n nghe hớt hớt trong tột c&ugrave;ng của bi thảm rồi im dần. Ả đứng trong liếc mắt nở nụ cười gian tr&aacute; v&agrave; h&agrave;i l&ograve;ng với m&agrave;n kịch m&agrave; m&igrave;nh đ&atilde; dựng l&ecirc;n. Ba mẹ ả nh&igrave;n nhau rồi n&oacute;i.</p>\r\n<p dir=\"ltr\">- M&agrave;y c&uacute;t ra khỏi nh&agrave; tao ngay, từ nay về sau cấm m&agrave;y quay lại căn nh&agrave; n&agrave;y, tao kh&ocirc;ng c&oacute; đứa con d&acirc;u lăng lo&agrave;n như m&agrave;y.</p>\r\n<p dir=\"ltr\">Chị bước ch&acirc;n ra đi như một kẻ mất hồn, cứ đi trong v&ocirc; định, chẳng biết phải đi về đ&acirc;u. Đ&ecirc;m đ&oacute; chị lang thang tr&ecirc;n khắp con phố, đ&oacute;i bụng, mệt r&atilde; rời, chẳng biết tr&uacute;t t&acirc;m sự chia sớt c&ugrave;ng ai. Trời đổ mưa phố x&aacute; lạnh như giữa m&ugrave;a đ&ocirc;ng. Chị vội v&agrave;ng chạy v&agrave;o gầm cầu, ngồi co ro hai tay &ocirc;m lấy hai ch&acirc;n, gục đầu xuống gối như một b&oacute;ng ma giữa đ&ecirc;m trường. Cuộc đời chị chưa bao giờ th&ecirc; thảm như đ&ecirc;m nay. B&acirc;y giờ chị c&oacute; kh&aacute;c g&igrave; như một kẻ ăn m&agrave;y, kh&ocirc;ng nh&agrave; kh&ocirc;ng cửa, nằm co ro dưới gầm cầu, ai đi ngang thương t&igrave;nh n&eacute;m cho chị ổ b&aacute;nh m&igrave;, v&agrave;i chai nước chị đều lấy như một người ăn m&agrave;y. Ng&agrave;y n&agrave;o vẫn c&ograve;n l&agrave; một b&agrave; chủ, ăn mặc đẹp xinh, c&oacute; rất nhiều tiền, c&oacute; một m&aacute;i ấm gia đ&igrave;nh hạnh ph&uacute;c, thế m&agrave; b&acirc;y giờ ra chị ra n&ocirc;ng nỗi tồi t&agrave;n như thế n&agrave;y.</p>\r\n<p dir=\"ltr\">Đ&ocirc;i khi nhớ chồng, nhớ gia đ&igrave;nh trở về thăm, nhưng lần n&agrave;o về cũng bị nh&agrave; chồng đuổi kh&ocirc;ng cho v&agrave;o nh&agrave;, chị chỉ c&ograve;n biết đứng trước cổng nh&igrave;n v&agrave;o căn nh&agrave; đ&atilde; kh&oacute;a cửa m&agrave; miệng &uacute; ớ. Kể từ đ&oacute;, chị &iacute;t n&oacute;i chuyện, miệng lu&ocirc;n lu&ocirc;n cười, đi lang thang khắp nơi, c&oacute; khi ca h&aacute;t nhảy m&uacute;a rồi tự vỗ tay, c&oacute; khi tự n&oacute;i chuyện với c&aacute;i b&oacute;ng của ch&iacute;nh m&igrave;nh.</p>\r\n<p dir=\"ltr\">Sau khi đuổi Li&ecirc;n ra khỏi nh&agrave;, ba mẹ của ả cũng thấy buồn, v&igrave; ng&agrave;y n&agrave;o Li&ecirc;n c&ograve;n cũng chu to&agrave;n mọi việc trong nh&agrave;, m&agrave; giờ bề bộn chưa từng thấy. Ri&ecirc;ng ả th&igrave; vẫn t&iacute;nh n&agrave;o tật nấy, vẫn như ăn chơi như cũ, vẫn th&acirc;u đ&ecirc;m suốt s&aacute;ng thường xuy&ecirc;n vắng nh&agrave;. Ba mẹ của ả bị bệnh kh&ocirc;ng ai chăm s&oacute;c, bỗng nhi&ecirc;n họ thấy nhớ Li&ecirc;n v&ocirc; c&ugrave;ng, giờ th&igrave; hai vợ chồng phải tự t&uacute;c chăm s&oacute;c nhau v&agrave; c&ograve;n phải chăm s&oacute;c th&ecirc;m anh hai của ả, mọi việc rối ren cả l&ecirc;n từ l&uacute;c Li&ecirc;n bị đuổi ra khỏi nh&agrave;.</p>\r\n<p dir=\"ltr\">Một h&ocirc;m ba mẹ ả đưa anh hai đi bệnh viện tr&ecirc;n một chiếc xe hơi, đến một đoạn xe bị hư phải dừng lại, ba ả gọi mọi người xuống xe v&agrave; đ&oacute;n chiếc taxi đi cho kịp giờ kh&aacute;m bệnh, việc xe hư để về rồi t&iacute;nh. &Ocirc;ng vẫy chiếc taxi b&ecirc;n kia đường, cả ba người đều l&ecirc;n taxi đi, khi t&agrave;i xế quay mặt lại hỏi.</p>\r\n<p dir=\"ltr\">- Hai b&aacute;c v&agrave; anh đi tới đ&acirc;u.</p>\r\n<p dir=\"ltr\">Ba mẹ ả như đứng h&igrave;nh khi nhận ra, t&agrave;i xế n&agrave;y ch&iacute;nh l&agrave; người h&ocirc;m đ&oacute; đ&atilde; v&agrave;o nh&agrave; &ocirc;ng, người m&agrave; con g&aacute;i &ocirc;ng đ&atilde; cho rằng ngoại t&igrave;nh với Li&ecirc;n. Hai &ocirc;ng b&agrave; vẫn nhớ gương mặt của hắn l&uacute;c chạy ra, ở trần, mặc quần đ&ugrave;i, tay cầm c&aacute;i quần d&agrave;i v&agrave; chiếc &aacute;o, kh&ocirc;ng thể lầm v&agrave;o đ&acirc;u được, ch&iacute;nh hắn. H&igrave;nh như hắn ho&agrave;n to&agrave;n kh&ocirc;ng nhận ra hai &ocirc;ng b&agrave;. &Ocirc;ng trả lời với hắn.</p>\r\n<p dir=\"ltr\">- T&ocirc;i đến bệnh viện Chợ Rẫy, phiền anh đưa t&ocirc;i đến đ&oacute;.</p>\r\n<p dir=\"ltr\">Hắn gật đầu rồi l&aacute;i xe, tr&ecirc;n đường đi hắn li&ecirc;n tục nhận được nhiều cuộc điện thoại. Ri&ecirc;ng c&oacute; một cuộc gọi l&agrave;m hai &ocirc;ng b&agrave; chết điếng.</p>\r\n<p dir=\"ltr\">- A l&ocirc; Phương hả, c&aacute;i tiền c&ocirc; thu&ecirc; t&ocirc;i v&agrave;o ph&ograve;ng của chị d&acirc;u c&ocirc; để vu khống ngoại t&igrave;nh, c&ocirc; t&iacute;nh khi n&agrave;o trả đ&acirc;y.</p>\r\n<p dir=\"ltr\">Hai &ocirc;ng b&agrave; v&agrave; cả anh hai ả, cứ nh&igrave;n nhau trong đau đớn tột c&ugrave;ng. Hai &ocirc;ng b&agrave; y&ecirc;u cầu dừng xe để n&oacute;i chuyện.</p>\r\n<p dir=\"ltr\">- Ch&uacute;ng t&ocirc;i l&agrave; người th&acirc;n của Phương đ&acirc;y, n&oacute; nợ anh bao nhi&ecirc;u, ch&uacute;ng t&ocirc;i trả.</p>\r\n<p dir=\"ltr\">Hắn giật m&igrave;nh khi được nghe giới thiệu l&agrave; người th&acirc;n của Phương, Hắn cứ ấp &uacute;ng.</p>\r\n<p dir=\"ltr\">- &Agrave; kh&ocirc;ng c&oacute; g&igrave; đ&acirc;u hai b&aacute;c. Phương kia k&igrave;a, kh&ocirc;ng phải con của hai b&aacute;c.</p>\r\n<p dir=\"ltr\">Mẹ của ả nghi&ecirc;m giọng với hắn.</p>\r\n<p dir=\"ltr\">- Nếu anh kh&ocirc;ng n&oacute;i, ch&uacute;ng t&ocirc;i sẽ b&aacute;o c&ocirc;ng an, v&agrave; tr&igrave;nh b&agrave;y mọi chuyện h&ocirc;m đ&oacute; với h&atilde;ng taxi nơi anh đang l&agrave;m việc. Nếu anh n&oacute;i sự thật tiền của Phương t&ocirc;i trả thay n&oacute; v&agrave; tặng ri&ecirc;ng anh th&ecirc;m một số tiền.</p>\r\n<p dir=\"ltr\">Hắn cứ do dự v&agrave; cuối c&ugrave;ng hắn cũng khai thật, v&igrave; kh&ocirc;ng đường n&agrave;o để tho&aacute;t khi c&ocirc;ng việc của hắn bị đe dọa, v&agrave; cả tr&aacute;ch nhiệm d&acirc;n sự.</p>\r\n<p dir=\"ltr\">- Con của hai &ocirc;ng b&agrave;, thu&ecirc; t&ocirc;i v&agrave;o ph&ograve;ng chị d&acirc;u c&ocirc; ấy, để vu khống rằng chị d&acirc;u c&ocirc; ấy ngoại t&igrave;nh với t&ocirc;i.</p>\r\n<p dir=\"ltr\">Ba của ả tiếp tục d&ograve; x&eacute;t hỏi.</p>\r\n<p dir=\"ltr\">- Nhưng anh v&agrave;o nh&agrave; t&ocirc;i bằng c&aacute;ch n&agrave;o.</p>\r\n<p dir=\"ltr\">- L&uacute;c 5 giờ s&aacute;ng, Phương ra mở cửa ch&iacute;nh cho t&ocirc;i, giờ đ&oacute; con d&acirc;u của hai &ocirc;ng b&agrave; xuống bếp l&agrave;m c&ocirc;ng chuyện, cửa ph&ograve;ng kh&ocirc;ng kh&oacute;a, n&ecirc;n t&ocirc;i v&agrave;o v&agrave; chui trong tủ đồ, đợi khi n&agrave;o Phương nh&aacute;y m&aacute;y l&agrave;m hiệu, t&ocirc;i sẽ bước ra đ&uacute;ng với kế hoạch của Phương đ&atilde; giao cho t&ocirc;i. T&ocirc;i chỉ l&agrave;m việc theo y&ecirc;u cầu của Phương với gi&aacute; 10 triệu đồng, ngo&agrave;i ra t&ocirc;i kh&ocirc;ng biết g&igrave; nữa, xin &ocirc;ng b&agrave; bỏ qua cho.</p>\r\n<p dir=\"ltr\">Cả ba người nghe như tiếng s&eacute;t b&ecirc;n tai, anh hai của ả đ&atilde; ngất xỉu ngay tại chỗ, mẹ của ả kh&oacute;c l&ecirc;n kh&oacute;c xuống, ba ả cũng kh&oacute;c như một đứa trẻ con, &ocirc;ng h&eacute;t lớn l&ecirc;n.</p>\r\n<p dir=\"ltr\">- Li&ecirc;n, con đang ở đ&acirc;u, ba xin lỗi con rất nhiều... Li&ecirc;n ơi...con d&acirc;u hiếu thảo của ba. H&atilde;y tha lỗi cha ba.</p>\r\n<p dir=\"ltr\">Đưa anh hai ả đến bệnh viện trong t&igrave;nh trạng nguy kịch phải thở bằng oxy, đ&ocirc;i mắt anh hai ả nước mắt lăn d&agrave;i xuống chiếc băng ca. Chiều đ&oacute; ba mẹ ả, tức tốc đ&oacute;n xe đến Chợ Lớn để gặp &ocirc;ng sui n&oacute;i ra sự thật v&agrave; cả lời xin lỗi. Ba mẹ ả tr&igrave;nh b&agrave;y đầu đu&ocirc;i c&acirc;u chuyện cho &ocirc;ng sui nghe, nghe xong ba mẹ ruột của Li&ecirc;n kh&oacute;c như mưa. Ba của Li&ecirc;n tự t&aacute;t v&agrave;o mặt m&igrave;nh rồi gọi.</p>\r\n<p dir=\"ltr\">- Li&ecirc;n ơi, đứa con tội nghiệp của ba.</p>\r\n<p dir=\"ltr\">Mẹ của Li&ecirc;n n&oacute;i trong hoảng loạn.</p>\r\n<p dir=\"ltr\">- Li&ecirc;n con t&ocirc;i... Li&ecirc;n con t&ocirc;i. T&ocirc;i phải đi t&igrave;m con t&ocirc;i, t&ocirc;i phải đi t&igrave;m con t&ocirc;i.</p>\r\n<p dir=\"ltr\">Mẹ của Li&ecirc;n chạy ra khỏi nh&agrave;, c&ograve;n lại ba người c&ugrave;ng kh&oacute;c tr&ecirc;n c&ugrave;ng một nỗi đau. Ng&agrave;y h&ocirc;m đ&oacute; anh hai của ả kh&ocirc;ng chịu được c&uacute; sốc n&ecirc;n đ&atilde; mất ngay tại bệnh viện. Một th&aacute;ng sau ba của ả v&igrave; qu&aacute; đau buồn với nỗi thương nhớ con v&agrave; lương t&acirc;m ray rứt, n&ecirc;n cũng đ&atilde; mất. Kh&ocirc;ng l&acirc;u sau đ&oacute; mẹ của ả kh&ocirc;ng c&ograve;n ai chăm s&oacute;c mỗi khi bệnh tật n&ecirc;n cũng đi theo ba ả.</p>\r\n<p dir=\"ltr\">Những lời người xung quanh kể về chị t&ocirc;i muốn rớt hai h&agrave;ng nước mắt. T&ocirc;i đồng cảm c&ugrave;ng nỗi l&ograve;ng người phụ nữ sau khi trải qua những niềm đau tột c&ugrave;ng. V&agrave;o một chiều t&ocirc;i trở lại nơi cũ, bầu trời k&eacute;o m&acirc;y đen giăng k&iacute;n, sắp c&oacute; một cơn mưa thật lớn như &ocirc;ng trời kh&oacute;c thương cho số phận của chị. Chị vẫn c&ograve;n c&ograve;n lang thang nơi đ&oacute;, t&ocirc;i bước tới gần nắm lấy b&agrave;n tay của chị, đ&ocirc;i tay gầy guộc trơ xương nhưng đ&atilde; chạm v&agrave;o s&oacute;ng gi&oacute; v&agrave; những cay đắng nhất của cuộc đời. Ba mẹ ruột đ&atilde; từng đ&oacute;n chị về nh&agrave; chăm s&oacute;c v&agrave; chữa trị, nhưng chị lại muốn lang thang như kh&ocirc;ng c&oacute; sự r&agrave;ng buộc n&agrave;o. Phụ nữ l&agrave; phải đẹp, phải kiếm thật nhiều tiền, kh&ocirc;ng lệ thuộc v&agrave;o ai, chăm ch&uacute;t v&agrave; y&ecirc;u bản th&acirc;n m&igrave;nh, nhưng chị kh&ocirc;ng được điều đ&oacute;, chị giờ như một đứa trẻ m&agrave; miệng đời gọi l&agrave; \" người đi&ecirc;n\" kh&ocirc;ng suy, kh&ocirc;ng nghĩ, kh&ocirc;ng toan t&iacute;nh, kh&ocirc;ng dối tr&aacute;, chỉ biết cười v&agrave; cười. Cũng tốt th&ocirc;i n&oacute; gi&uacute;p chị qu&ecirc;n đi một k&iacute; ức rất đau buồn, đ&oacute; l&agrave; nỗi &aacute;m ảnh m&agrave; chẳng ai muốn nhớ tới, chị vẫn giữ nụ cười rất hồn nhi&ecirc;n, n&oacute; hồn nhi&ecirc;n nhưng chưa từng c&oacute; chuyện g&igrave; xảy ra trong cuộc đời chị.</p>\r\n<p dir=\"ltr\">Đ&ocirc;i khi trong cuộc sống người ta mệt mỏi với những thứ lo toan, &aacute;p lực, v&agrave; nỗi buồn đang đ&egrave; l&ecirc;n tinh thần lẫn th&acirc;n x&aacute;c họ. Họ cũng muốn được như chị để qu&ecirc;n hết tất cả, đổi lại sự b&igrave;nh y&ecirc;n giữa gi&ocirc;ng b&atilde;o cuộc đời. Cơn mưa đ&atilde; đến, chị th&iacute;ch th&uacute; dang tay ca h&aacute;t nhảy m&uacute;a, chị vui trong niềm vui của ri&ecirc;ng chị, chẳng ai biết đ&oacute; l&agrave; niềm vui g&igrave;. Chị vẫn cười, vẫn ngh&ecirc;u ngao h&aacute;t, chị đi về cuối con đường cười đ&ugrave;a h&ograve;a m&igrave;nh v&agrave;o cơn mưa chiều hối hả của S&agrave;i G&ograve;n. D&aacute;ng chị mịt mờ như l&agrave;n sương ảo, nhưng d&aacute;ng ấy sẽ hiện r&otilde; trong t&acirc;m tr&iacute; t&ocirc;i từ đ&acirc;y v&agrave; cả về sau n&agrave;y.</p>\r\n<p>&nbsp;</p>','2022-05-31 15:04:42.755000','p5ac638f3-19bb-4b29-821a-43da17fc34c2.png','NGƯỜI PHỤ NỮ DƯỚI CƠN MƯA',7);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (20,'<p dir=\"ltr\">C&oacute; một học tr&ograve; hỏi thầy m&igrave;nh rằng:</p>\r\n<p dir=\"ltr\">- Thưa thầy, gi&aacute; trị của cuộc sống l&agrave; g&igrave; ạ?</p>\r\n<p dir=\"ltr\">Người thầy lấy một h&ograve;n đ&aacute; trao cho người học tr&ograve; v&agrave; dặn:</p>\r\n<p dir=\"ltr\">- Con đem h&ograve;n đ&aacute; n&agrave;y ra chợ nhưng kh&ocirc;ng được b&aacute;n n&oacute; đi, chỉ cần để &yacute; xem người ta trả gi&aacute; bao nhi&ecirc;u.</p>\r\n<p dir=\"ltr\">V&acirc;ng lời thầy, người học tr&ograve; mang h&ograve;n đ&aacute; ra chợ b&aacute;n. Mọi người kh&ocirc;ng hiểu tại sao anh lại b&aacute;n một h&ograve;n đ&aacute; xấu x&iacute; như vậy. Ngồi cả ng&agrave;y, một người b&aacute;n rong thương t&igrave;nh đ&atilde; đến hỏi v&agrave; trả gi&aacute; h&ograve;n đ&aacute; một đồng. Người học tr&ograve; mang h&ograve;n đ&aacute; về v&agrave; than thở:</p>\r\n<p dir=\"ltr\">- H&ograve;n đ&aacute; xấu x&iacute; n&agrave;y chẳng ai th&egrave;m mua. Cũng may c&oacute; ngườihỏi mua với gi&aacute; một đồng thầy ạ.</p>\r\n<p dir=\"ltr\">Người thầy mỉm cười v&agrave; n&oacute;i:</p>\r\n<p dir=\"ltr\">- Tốt lắm, ng&agrave;y mai con h&atilde;y mang h&ograve;n đ&aacute; v&agrave;o tiệm v&agrave;ng v&agrave; b&aacute;n cho chủ tiệm, nhớ l&agrave; d&ugrave; chủ cửa h&agrave;ng v&agrave;ng c&oacute; mua th&igrave; cũng kh&ocirc;ng được b&aacute;n.</p>\r\n<p dir=\"ltr\">Người học tr&ograve; rất bất ngờ khi chủ tiệm v&agrave;ng trả gi&aacute; h&ograve;n đ&aacute; l&agrave; 500 đồng. Anh h&aacute;o hức hỏi thầy tại sao lại như vậy. Người thầy cười v&agrave; n&oacute;i:</p>\r\n<p dir=\"ltr\">- Ng&agrave;y mai con h&atilde;y đem n&oacute; đến chỗ b&aacute;n đồ cổ. Nhưng tuyệt đối đừng b&aacute;n n&oacute;, chỉ hỏi gi&aacute; m&agrave; th&ocirc;i.</p>\r\n<p dir=\"ltr\">L&agrave;m theo lời thầy dặn, sau một hồi xem x&eacute;t th&igrave; anh v&ocirc; c&ugrave;ng ngạc nhi&ecirc;n khi chủ hiệu trả gi&aacute; h&ograve;n đ&aacute; l&agrave; cả gia sản hiện c&oacute;. Anh vẫn nhất quyết kh&ocirc;ng b&aacute;n v&agrave; vội về kể lại với thầy. L&uacute;c n&agrave;y người thầy mới chậm r&atilde;i n&oacute;i:</p>\r\n<p dir=\"ltr\">- H&ograve;n đ&aacute; thực chất ch&iacute;nh l&agrave; một khối ngọc cổ qu&yacute; gi&aacute;, đ&aacute;ng cả một gia t&agrave;i, v&agrave; gi&aacute; trị cuộc sống cũng giống như h&ograve;n đ&aacute; kia, c&oacute; người hiểu v&agrave; c&oacute; người kh&ocirc;ng hiểu. Với người kh&ocirc;ng hiểu v&agrave; kh&ocirc;ng thể cảm nhận th&igrave; gi&aacute; trị cuộc sống chẳng đ&aacute;ng một xu, c&ograve;n với người hiểu th&igrave; n&oacute; đ&aacute;ng gi&aacute; cả một gia t&agrave;i. H&ograve;n đ&aacute; vẫn vậy, cuộc sống vẫn thế, điều duy nhất tạo n&ecirc;n sự kh&aacute;c biệt l&agrave; sự hiểu biết của con v&agrave; c&aacute;ch con nh&igrave;n nhận cuộc sống.</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">B&agrave;i học: Mỗi người c&oacute; một c&aacute;ch &ldquo;định gi&aacute;&rdquo; kh&aacute;c nhau về th&agrave;nh c&ocirc;ng hay hạnh ph&uacute;c. H&atilde;y t&ocirc;n trọng sự lựa chọn của mỗi người v&agrave; l&agrave;m cho đời m&igrave;nh trở n&ecirc;n gi&aacute; trị theo c&aacute;ch của m&igrave;nh. Chỉ c&oacute; bạn mới quyết đinh được cuộc sống của bạn</p>\r\n<p>&nbsp;</p>','2022-05-31 15:06:02.619000','pcf0b9325-3c07-40ca-93ed-a45da9245e66.png','Giá trị của hòn đá',7);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (21,'<p dir=\"ltr\">Một buổi chiều nọ, c&oacute; một ch&uacute; c&aacute;o đang dạo bước trong rừng v&agrave; ch&uacute; ph&aacute;t hiện ra một ch&ugrave;m nho treo lơ lửng tr&ecirc;n c&agrave;nh c&acirc;y cao. C&aacute;o th&egrave;m tới mức nước bọt cứ tr&agrave;o ra hai b&ecirc;n m&eacute;p.</p>\r\n<p dir=\"ltr\">\"Thứ n&agrave;y sẽ l&agrave;m dịu cơn kh&aacute;t của m&igrave;nh đ&acirc;y\", ch&uacute; nghĩ.</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">L&ugrave;i lại v&agrave;i bước, ch&uacute; c&aacute;o ph&oacute;ng người l&ecirc;n nhưng kh&ocirc;ng h&aacute;i được ch&ugrave;m nho n&agrave;o m&agrave; chỉ tới l&aacute; nho m&agrave; th&ocirc;i. Một lần nữa, ch&uacute; c&aacute;o lại l&ugrave;i lại v&agrave;i bước v&agrave; cố gắng để h&aacute;i được ch&ugrave;m nho nhưng vẫn thất bại. C&aacute;o ta t&igrave;m đủ mọi c&aacute;ch nhưng vẫn kh&ocirc;ng thể với tới được ch&ugrave;m nho.</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">Cuối c&ugrave;ng, bỏ cuộc, ch&uacute; c&aacute;o hếch mũi l&ecirc;n v&agrave; n&oacute;i: \"D&ugrave; g&igrave; th&igrave; cũng chỉ l&agrave; những tr&aacute;i nho chua m&agrave; th&ocirc;i\", rồi cứ thế bỏ đi.</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">B&agrave;i học: Thật dễ d&agrave;ng để coi thường những g&igrave; bạn kh&ocirc;ng thể c&oacute; được. Một số người khi kh&ocirc;ng c&oacute; được thứ g&igrave; đ&oacute; liền n&oacute;i thứ đ&oacute; kh&ocirc;ng ra g&igrave; cả. Thực tế th&igrave; chỉ l&agrave; v&igrave; khả năng của m&igrave;nh c&oacute; giới hạn kh&ocirc;ng thể c&oacute; được nhưng đ&agrave;nh lấy cớ, tự dối l&ograve;ng m&igrave;nh để tự biện minh.</p>\r\n<p>&nbsp;</p>','2022-05-31 15:06:59.877000','pe5e44dda-c722-4a01-8a63-aeb7eb669192.png','Con cáo và chùm nho',7);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (22,'<p dir=\"ltr\">C&oacute; hai hạt l&uacute;a nọ được giữ lại để l&agrave;m hạt giống cho vụ sau v&igrave; cả hai đều l&agrave; những hạt l&uacute;a tốt, đều to khỏe v&agrave; chắc mẩy,...</p>\r\n<p dir=\"ltr\">Một h&ocirc;m, người chủ định đem ch&uacute;ng gieo tr&ecirc;n c&aacute;nh đồng gần đ&oacute;. Hạt thứ nhất nhủ thầm:</p>\r\n<p dir=\"ltr\">&ldquo;Dại g&igrave; ta phải theo &ocirc;ng chủ ra đồng. Ta kh&ocirc;ng muốn cả th&acirc;n m&igrave;nh phải n&aacute;t tan trong đất. Tốt nhất ta h&atilde;y giữ lại tất cả chất dinh dưỡng trong lớp vỏ n&agrave;y v&agrave; t&igrave;m một nơi l&yacute; tưởng để tr&uacute; ngụ.&rdquo; Thế l&agrave; n&oacute; chọn một g&oacute;c khuất trong kho l&uacute;a để lăn v&agrave;o đ&oacute;.</p>\r\n<p dir=\"ltr\">C&ograve;n hạt l&uacute;a thứ hai th&igrave; ng&agrave;y đ&ecirc;m mong được &ocirc;ng chủ mang gieo xuống đất. N&oacute; thật sự sung sướng khi được bắt đầu một cuộc đời mới.</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">Thời gian tr&ocirc;i qua, hạt l&uacute;a thứ nhất bị h&eacute;o kh&ocirc; nơi g&oacute;c nh&agrave; bởi v&igrave; n&oacute; chẳng nhận được nước v&agrave; &aacute;nh s&aacute;ng. L&uacute;c n&agrave;y chất dinh dưỡng chẳng gi&uacute;p &iacute;ch được g&igrave; - n&oacute; chết dần chết m&ograve;n. Trong khi đ&oacute;, hạt l&uacute;a thứ hai d&ugrave; n&aacute;t tan trong đất nhưng từ th&acirc;n n&oacute; lại mọc l&ecirc;n c&acirc;y l&uacute;a v&agrave;ng &oacute;ng, trĩu hạt. N&oacute; lại mang đến cho đời những hạt l&uacute;a mới...</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">B&agrave;i học: Đừng bao giờ tự kh&eacute;p m&igrave;nh trong lớp vỏ chắc chắn để cố giữ sự nguy&ecirc;n vẹn v&ocirc; nghĩa của bản th&acirc;n m&agrave; h&atilde;y can đảm bước đi, &acirc;m thầm chịu n&aacute;t tan để g&oacute;p cho c&aacute;nh đồng cuộc đời một c&acirc;y l&uacute;a nhỏ - đ&oacute; l&agrave; sự chọn lựa của hạt giống thứ hai. Trước c&aacute;nh đồng cuộc đời bao la n&agrave;y t&ocirc;i hy vọng ch&uacute;ng ta mỗi người sẽ c&oacute; những sự lựa chọn cho ri&ecirc;ng bản th&acirc;n m&igrave;nh.</p>\r\n<p>&nbsp;</p>','2022-05-31 15:08:02.544000','p10918d76-36e9-48d1-9624-53ed4a15a535.png','Câu chuyện về hai hạt lúa',7);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (23,'<p dir=\"ltr\">Trong 1 lần, con c&aacute;o v&agrave; con c&ograve; đ&atilde; c&oacute; cuộc gh&eacute; thăm nhau v&agrave; ch&uacute;ng dường như rất th&acirc;n với nhau. Rồi con c&aacute;o cũng đ&atilde; mời c&ograve; ăn tối v&agrave; c&oacute; một tr&ograve; đ&ugrave;a nhỏ trước mặt c&ograve; l&agrave; cho s&uacute;p v&agrave;o một c&aacute;i ch&eacute;n cạn.</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">Con c&aacute;o rất dễ d&agrave;ng để hớp thức ăn, nhưng con c&ograve; th&igrave; chỉ c&oacute; thể chấm ướt phần đầu của c&aacute;i miệng d&agrave;i v&agrave; cuối c&ugrave;ng thức ăn vẫn c&ograve;n c&ograve;n nguy&ecirc;n như l&uacute;c c&ograve; mới bắt đầu ăn.</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">C&aacute;o n&oacute;i \"T&ocirc;i xin lỗi, m&oacute;n s&uacute;p kh&ocirc;ng phải l&agrave; m&oacute;n bạn y&ecirc;u th&iacute;ch ư\".</p>\r\n<p dir=\"ltr\">\"Bạn n&ecirc;n cầu nguyện chứ đừng xin lỗi,\" C&ograve; n&oacute;i \"T&ocirc;i hy vọng lần sau sẽ sớm tới lượt bạn gh&eacute; thăm v&agrave; ăn tối với t&ocirc;i nha\".</p>\r\n<p dir=\"ltr\">Rồi một ng&agrave;y được định sẵn c&aacute;o sẽ tới thăm c&ograve;, nhưng khi họ ngồi xuống b&agrave;n th&igrave; tất cả đồ ăn cho buổi tối của họ như được đựng trong một c&aacute;i lọ c&oacute; cổ rất d&agrave;i với miệng hẹp.</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">C&aacute;o kh&ocirc;ng thể thưởng thức v&igrave; vậy tất cả những điều m&agrave; anh ta c&oacute; thể l&agrave;m l&agrave; liếm b&ecirc;n ngo&agrave;i c&aacute;i lọ.</p>\r\n<p dir=\"ltr\">\"T&ocirc;i sẽ kh&ocirc;ng xin lỗi v&igrave; bữa tối.\" C&ograve; n&oacute;i.</p>\r\n<p dir=\"ltr\">&nbsp;</p>\r\n<p dir=\"ltr\">B&agrave;i học: Gieo nh&acirc;n n&agrave;o gặp quả đấy. Khi bạn g&acirc;y ra một điều tồi tệ với người kh&aacute;c sẽ c&oacute; một điều tồi tệ tương tự sẽ đến với bạn</p>\r\n<p>&nbsp;</p>','2022-05-31 15:09:53.592000','p4d0e7f44-5b05-461d-945b-1c9a2dcd8041.png','Con cáo và con cò',7);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (24,'<p dir=\"ltr\">Mẹ t&ocirc;i lu&ocirc;n nấu đồ ăn ngon. Nhưng h&ocirc;m nay mẹ đặt một c&aacute;i b&aacute;nh ch&aacute;y trước mặt bố. Kh&ocirc;ng chỉ hơi ch&aacute;y th&ocirc;i m&agrave; đen như than đ&aacute;.</p>\r\n<p dir=\"ltr\">Chờ xem bố t&ocirc;i sẽ n&oacute;i g&igrave;.</p>\r\n<p dir=\"ltr\">Nhưng bố t&ocirc;i vừa ăn b&aacute;nh v&agrave; hỏi \"ng&agrave;y h&ocirc;m nay của em thế n&agrave;o.\"</p>\r\n<p dir=\"ltr\">Vậy l&agrave; t&ocirc;i nghe mẹ t&ocirc;i xin lỗi bố v&igrave; chiếc b&aacute;nh đ&oacute; .</p>\r\n<p dir=\"ltr\">T&ocirc;i sẽ kh&ocirc;ng bao giờ qu&ecirc;n c&acirc;u trả lời của bố rằng:</p>\r\n<p dir=\"ltr\">- Kh&ocirc;ng sao cả, anh th&iacute;ch b&aacute;nh của em.</p>\r\n<p dir=\"ltr\">Sau đ&oacute; t&ocirc;i đ&atilde; hỏi liệu bố t&ocirc;i c&oacute; n&oacute;i sự thật hay kh&ocirc;ng.</p>\r\n<p dir=\"ltr\">Bố đặt tay l&ecirc;n vai t&ocirc;i v&agrave; n&oacute;i:</p>\r\n<p dir=\"ltr\">- Mẹ con h&ocirc;m nay đ&atilde; c&oacute; một ng&agrave;y kh&oacute; khăn ở chỗ l&agrave;m, b&agrave; ấy đ&atilde; rất mệt mỏi. Chiếc b&aacute;nh ch&aacute;y kh&ocirc;ng l&agrave;m tổn thương bố được, nhưng một lời n&oacute;i của bố c&oacute; thể sẽ l&agrave;m tổn thương mẹ con</p>\r\n<p dir=\"ltr\">Tất cả ch&uacute;ng ta đều mắc sai lầm. Ch&uacute;ng ta kh&ocirc;ng n&ecirc;n tập trung v&agrave;o sai lầm, m&agrave; h&atilde;y nhẹ nh&agrave;ng với những người ch&uacute;ng ta y&ecirc;u thương.</p>\r\n<p dir=\"ltr\">Đ&acirc;y l&agrave; b&iacute; quyết của những mối quan hệ l&acirc;u d&agrave;i v&agrave; hạnh ph&uacute;c.</p>\r\n<p>&nbsp;</p>','2022-05-31 15:11:48.639000','p70218556-2d0f-4e5e-8c0e-24ddc6583506.png','Lời nói dối ngọt ngào',7);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (25,'<p dir=\"ltr\">\"Con kh&ocirc;ng đi c&aacute;i xe đấy đ&acirc;u, xấu hổ lắm, bạn b&egrave; con to&agrave;n đi xe ga, mẹ mua xe ga con mới đi&hellip;.&rdquo;</p>\r\n<p dir=\"ltr\">C&acirc;u chuyện của hai mẹ con cự nự nhau sau lưng trong qu&aacute;n cafe trưa nay l&agrave;m t&ocirc;i bất gi&aacute;c c&oacute; một ch&uacute;t buồn, nhưng rồi lại chợt cảm thấy ấm l&ecirc;n một niềm vui khi nghĩ về một c&acirc;u chuyện tương tự của bố con t&ocirc;i hơn 10 năm về trước.</p>\r\n<p dir=\"ltr\">\"Bố cho con c&aacute;i g&igrave;?\" - Nhớ một thời trẻ tr&acirc;u, t&ocirc;i đ&atilde; c&oacute; đủ \"dũng cảm\" hỏi cha m&igrave;nh c&acirc;u đ&oacute;, lần đầu ti&ecirc;n v&agrave; cũng l&agrave; duy nhất. Đ&oacute; l&agrave; một ng&agrave;y kh&ocirc;ng l&acirc;u sau khi nhận tin đỗ v&agrave;o đại học. Một cuộc tr&ograve; chuyện rất nghi&ecirc;m t&uacute;c v&agrave; thẳng thắn giữa hai người đ&agrave;n &ocirc;ng.</p>\r\n<p dir=\"ltr\">Bố t&ocirc;i trả lời một c&aacute;ch kh&ocirc;ng thể b&igrave;nh thản hơn \"Bố mẹ bố cho bố c&aacute;i g&igrave;, bố sẽ cho lại con c&aacute;i đ&oacute;: một l&yacute; lịch trong sạch để con kh&ocirc;ng bao giờ phải xấu hổ về bố v&agrave; một sự gi&aacute;o dục tốt nhất trong khả năng của m&igrave;nh. Con c&oacute; khả năng học đến đ&acirc;u bố sẽ hỗ trợ đến đ&oacute;. Hết\"</p>\r\n<p dir=\"ltr\">T&ocirc;i, hơi sốc, nhưng vẫn nghĩ đ&oacute; chỉ l&agrave; c&acirc;u n&oacute;i \"l&ecirc;n d&acirc;y c&oacute;t\" cho ch&agrave;ng sinh vi&ecirc;n mới. V&agrave; rất tiếc l&agrave; bố t&ocirc;i chẳng đ&ugrave;a, bố h&agrave;nh động rất thật theo đ&uacute;ng những tuy&ecirc;n bố đấy. Bố t&iacute;nh to&aacute;n rất kỹ v&agrave; cho t&ocirc;i một khoản tiền trợ cấp 300 ngh&igrave;n/ th&aacute;ng trong suốt những năm học đại học. Tiền học ph&iacute; học kỳ đầu ti&ecirc;n được cho, từ học kỳ thứ 2 t&ocirc;i tự kiếm được n&ecirc;n tự động kh&ocirc;ng xin nữa. Bất kể những năm sau khi t&ocirc;i kiếm được nhiều tiền hơn gấp nhiều lần th&igrave; khoản trợ cấp đấy vẫn được duy tr&igrave; cho đến khi tốt nghiệp, nhận bằng l&agrave; cắt tiền.</p>\r\n<p dir=\"ltr\">6 năm t&ocirc;i đi học ở nước ngo&agrave;i, bố kh&ocirc;ng phải lo cho t&ocirc;i một đồng n&agrave;o. Với t&ocirc;i, bố lu&ocirc;n l&agrave; Napoleon c&ograve;n t&ocirc;i chỉ l&agrave; một anh binh nh&igrave;. Nhưng &iacute;t nhất t&ocirc;i lu&ocirc;n coi đ&oacute; như một chiến c&ocirc;ng nho nhỏ của ri&ecirc;ng m&igrave;nh.</p>\r\n<p dir=\"ltr\">Bố t&ocirc;i rất hay, lu&ocirc;n ph&acirc;n định rất r&otilde; r&agrave;ng: \"Đ&acirc;y l&agrave; nh&agrave; của bố nh&eacute;, đ&acirc;y l&agrave; xe của bố nh&eacute;. V&agrave; con đang... ở nhờ v&agrave; đi nhờ. Kh&ocirc;ng h&agrave;i l&ograve;ng hả, quyền đi bộ... lu&ocirc;n thuộc về con\".</p>\r\n<p dir=\"ltr\">Nếu nhờ t&ocirc;i gi&uacute;p việc g&igrave; kh&ocirc;ng nằm trong tr&aacute;ch nhiệm của con c&aacute;i, thay v&igrave; thu&ecirc; người ngo&agrave;i, bố sẽ thu&ecirc; t&ocirc;i l&agrave;m v&agrave; trả tiền rất s&ograve;ng phẳng, kh&ocirc;ng qu&ecirc;n thể hiện l&agrave; một kh&aacute;ch h&agrave;ng kh&oacute; t&iacute;nh. Kh&ocirc;ng tự &aacute;i, kh&ocirc;ng phiền l&ograve;ng, t&ocirc;i biết r&otilde; m&igrave;nh chỉ c&oacute; một con đường nếu muốn c&oacute; ng&ocirc;i nh&agrave; ri&ecirc;ng của m&igrave;nh: tự mua. Cũng c&oacute; người nghe thấy v&agrave; thắc mắc c&aacute;i kiểu n&oacute;i ấy: \"Nh&agrave; của b&aacute;c th&igrave; sau n&agrave;y kh&ocirc;ng của n&oacute; th&igrave; của ai, sao b&aacute;c lại n&oacute;i thế...\". V&agrave; bố t&ocirc;i chỉnh ngay: \"Của t&ocirc;i chứ, nếu n&oacute; kh&ocirc;ng cố gắng, t&ocirc;i sẽ cho từ thiện\". Bố t&ocirc;i th&igrave; chẳng gi&agrave;u như Bill Gates, nhưng d&aacute;m l&agrave;m như Bill Gates th&igrave; t&ocirc;i tin l&agrave; l&agrave;m thật.</p>\r\n<p dir=\"ltr\">Bữa ăn &iacute;t người của nh&agrave; t&ocirc;i lu&ocirc;n c&oacute; những c&acirc;u chuyện về c&aacute;c lo&agrave;i vật, những c&acirc;u chuyện được lặp đi lặp lại, được kể l&uacute;c n&agrave;y l&uacute;c kh&aacute;c.</p>\r\n<p dir=\"ltr\">Bố hay n&oacute;i chuyện: Con g&agrave; con đến tuổi tự kiếm ăn, g&agrave; mẹ sẽ đuổi chạy ch&iacute; chết nếu g&agrave; con cố đến gần hoặc đi theo. Hay c&acirc;u chuyện về lo&agrave;i đại b&agrave;ng: Đại b&agrave;ng con sẽ được mẹ nu&ocirc;i mớm trong tổ đến khi đủ l&ocirc;ng đủ c&aacute;nh, v&agrave; sau đ&oacute; n&oacute; sẽ cắp con bay l&ecirc;n đỉnh n&uacute;i thật cao v&agrave; thả xuống.</p>\r\n<p dir=\"ltr\">Con n&agrave;o chịu đập c&aacute;nh v&agrave;o kh&ocirc;ng trung v&agrave; bay đi th&igrave; sống v&agrave; bắt đầu cuộc đời mới, con n&agrave;o kh&ocirc;ng tự bay được th&igrave; sẽ tự rớt xuống v&agrave; vực thẳm sẽ chờ ở dưới. Quy luật tự nhi&ecirc;n l&agrave; vậy, v&agrave; con người l&agrave; một phần của tự nhi&ecirc;n, n&ecirc;n cũng kh&ocirc;ng l&agrave; ngoại lệ. M&ugrave;i răn đe trong những c&acirc;u chuyện thơm nức suốt những năm th&aacute;ng tuổi thơ t&ocirc;i.</p>\r\n<p dir=\"ltr\">Sự h&agrave;o ph&oacute;ng kh&ocirc;ng đ&uacute;ng chỗ của bố mẹ khiến con trở th&agrave;nh đứa trẻ yếu ớt, ỷ lại.</p>\r\n<p dir=\"ltr\">Những điều t&ocirc;i kể tr&ecirc;n đ&acirc;y với nhiều người, nhiều &ocirc;ng bố b&agrave; mẹ c&oacute; lẽ l&agrave; những điều ngược đời, tuy nhi&ecirc;n, bước một bước ra b&ecirc;n ngo&agrave;i thế giới, t&ocirc;i thấy m&igrave;nh h&oacute;a ra kh&ocirc;ng phải ngoại lệ. Phần đ&ocirc;ng c&aacute;c gia đ&igrave;nh phương T&acirc;y đều như vậy, tr&aacute;i ngược ho&agrave;n to&agrave;n với những g&igrave; ch&uacute;ng ta thấy ở phương Đ&ocirc;ng. Sự ph&acirc;n định rất r&otilde; r&agrave;ng giữa tr&aacute;ch nhiệm, t&igrave;nh thương, v&agrave; sự nu&ocirc;ng chiều l&agrave;m cho con người ta kh&ocirc;ng thể t&igrave;m thấy nổi một khoảnh khắc của sự ỷ lại hay tr&ocirc;ng chờ v&ocirc; l&yacute; ngay từ khi bước v&agrave;o đời.</p>\r\n<p dir=\"ltr\">Bạn kh&ocirc;ng c&oacute; tiền học đại học? Được th&ocirc;i, h&atilde;y vay đi rồi sau n&agrave;y tự trả. C&aacute;c bạn nước ngo&agrave;i của t&ocirc;i rất nhiều người chọn giải ph&aacute;p như vậy, mặc d&ugrave; rất nhiều bạn c&oacute; bố mẹ tr&ecirc;n cả gi&agrave;u v&agrave; lu&ocirc;n sẵn s&agrave;ng t&agrave;i trợ.</p>\r\n<p dir=\"ltr\">Sự h&agrave;o ph&oacute;ng kh&ocirc;ng đ&uacute;ng chỗ của rất đ&ocirc;ng c&aacute;c &ocirc;ng bố b&agrave; mẹ Việt giống như b&agrave; mẹ trong c&acirc;u chuyện l&uacute;c đầu của t&ocirc;i đang để lại cho đất nước những thế hệ yếu ớt, kh&ocirc;ng c&oacute; khả năng sống độc lập v&agrave; tự trọng với ch&iacute;nh người th&acirc;n của m&igrave;nh.</p>\r\n<p dir=\"ltr\">Họ nghiễm nhi&ecirc;n cho m&igrave;nh c&aacute;i quyền được xin xỏ, được v&ograve;i vĩnh, được lạm dụng v&ocirc; hạn t&igrave;nh y&ecirc;u thương của cha mẹ&hellip;&hellip;v&agrave; c&aacute;c vị phụ huynh th&igrave; vẫn cứ tin tưởng trong sai lầm rằng để cho con k&eacute;m bạn k&eacute;m b&egrave; ngay cả khi ch&uacute;ng đ&atilde; trưởng th&agrave;nh l&agrave; kh&ocirc;ng tr&ograve;n tr&aacute;ch nhiệm cha mẹ.</p>\r\n<p dir=\"ltr\">Ở nước m&igrave;nh, c&aacute;i v&ograve;ng luẩn quẩn ấy biết khi n&agrave;o mới th&ocirc;i? Cố gắng c&oacute; của cải để m&agrave; cho con đ&atilde; l&agrave; kh&oacute;, nhưng cố gắng để c&oacute; của cải m&agrave; vẫn kh&ocirc;ng cho th&igrave; c&ograve;n kh&oacute; gấp vạn lần. Nghe c&oacute; vẻ tr&aacute;i với quy luật của t&igrave;nh cảm con người, nhưng đ&oacute; l&agrave; một sự ngược chiều cần thiết. Điều đ&oacute; c&oacute; lẽ thuộc về bản lĩnh của nghề l&agrave;m cha mẹ.</p>\r\n<p dir=\"ltr\">Rất nhiều l&uacute;c t&ocirc;i đ&atilde; tự hỏi m&igrave;nh &ldquo;Vậy sau c&ugrave;ng, bố sẽ cho m&igrave;nh c&aacute;i g&igrave; nhỉ?&rdquo;</p>\r\n<p dir=\"ltr\">V&agrave; mười năm sau cuộc n&oacute;i chuyện s&ograve;ng phẳng đấy, v&agrave;o l&uacute;c t&ocirc;i tự mua được căn nh&agrave; v&agrave; chiếc xe hơi đầu ti&ecirc;n của ri&ecirc;ng m&igrave;nh m&agrave; chẳng phải xin xỏ g&igrave; bố, t&ocirc;i mới thấu hiểu hết t&igrave;nh thương v&ocirc; bờ bến v&agrave; gia t&agrave;i v&ocirc; gi&aacute; m&agrave; Bố đ&atilde; để d&agrave;nh cho ri&ecirc;ng t&ocirc;i mấy chục năm nay.</p>\r\n<p dir=\"ltr\">Cho l&ograve;ng tự trọng v&agrave; tinh thần tự lực đ&atilde; l&agrave; cho tất cả rồi.</p>\r\n<p>&nbsp;</p>','2022-05-31 15:14:21.728000','pa4b2c667-038c-4c89-945a-6ab286d2df55.png','BỐ CHO CON CÁI GÌ?',10);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (26,'<p dir=\"ltr\">- &ldquo;&Ecirc; m&agrave;y, đ&acirc;y l&agrave; bữa cuối c&ugrave;ng bọn m&igrave;nh được ch&agrave;o cờ dưới s&acirc;n trường rồi &aacute;.&rdquo;</p>\r\n<p dir=\"ltr\">- &ldquo;Ờ, đ&acirc;y l&agrave; buổi ch&agrave;o cờ cuối c&ugrave;ng rồi nhỉ.&rdquo;</p>\r\n<p dir=\"ltr\">Do diễn biến phức tạp của dịch Covid, m&agrave; năm cuối cấp của ch&uacute;ng t&ocirc;i mới được ch&agrave;o cờ c&ugrave;ng nhau mới vỏn vẹn c&oacute; mấy buổi...Chưa bao giờ K22 ch&uacute;ng t&ocirc;i nghĩ rằng, buổi ch&agrave;o cờ dưới s&acirc;n trường đầy nắng l&agrave; một thứ xa xỉ với đời học sinh cuối cấp v&agrave; c&agrave;ng kh&ocirc;ng tin rằng buổi ch&agrave;o cờ ng&agrave;y h&ocirc;m ấy cũng ch&iacute;nh l&agrave; buổi ch&agrave;o cờ cuối c&ugrave;ng của đời học sinh.</p>\r\n<p dir=\"ltr\">Trong &aacute;nh mắt đầy tinh kh&ocirc;i của c&aacute;c em học sinh khối 10, 11 th&igrave; c&oacute; lẽ buổi ch&agrave;o cờ vẫn như bao lần vậy th&ocirc;i. Đ&oacute; l&agrave; c&aacute;i nắng oi ả, l&agrave; những t&agrave; &aacute;o d&agrave;i trắng, l&agrave; l&aacute; cờ đỏ bay phấp phới, l&agrave; tiếng đ&agrave;n, tiếng h&aacute;t h&ograve;a v&agrave;o nhau, l&agrave; tiếng thầy c&ocirc; &ocirc;n tồn tr&ecirc;n ph&iacute;a bục ph&aacute;t biểu hay những tiếng r&iacute;u ra r&iacute;u r&iacute;t của hơn một ng&agrave;n con người ngồi san s&aacute;t nhau.</p>\r\n<p dir=\"ltr\">C&ograve;n giờ đ&acirc;y, điều g&igrave; c&ograve;n lại trong tr&aacute;i tim của những c&ocirc; cậu học tr&ograve; lớp 12?</p>\r\n<p dir=\"ltr\">Bỗng chốc cảm thấy, những quan cảnh v&agrave; &acirc;m thanh t&ocirc;i tưởng rằng m&igrave;nh đ&atilde; ch&aacute;n ngấy, lại hiện l&ecirc;n với một vẻ đẹp đầy gợi nhớ v&agrave; trong trẻo. H&aacute;o hức, nhưng lại bồn chồn v&agrave; thất vọng, như c&oacute; ai đang lấy mất một phần trong t&ocirc;i.</p>\r\n<p dir=\"ltr\">Kh&ocirc;ng d&aacute;m tin, cũng kh&ocirc;ng d&aacute;m đối mặt, rằng đ&oacute; l&agrave; một trong số những khoảnh khắc cuối c&ugrave;ng đại diện cho 3 năm thanh xu&acirc;n của t&ocirc;i. L&agrave; khi đ&atilde; tr&ocirc;i qua rồi, ngoảnh mặt lại chỉ c&ograve;n một mảnh k&yacute; ức, kh&ocirc;ng thể lặp lại cũng kh&ocirc;ng thể v&atilde;n hồi.</p>\r\n<p dir=\"ltr\">Ch&uacute;ng t&ocirc;i đắm ch&igrave;m trong sự đăm chi&ecirc;u của tuổi 18, ngưỡng cửa của sự trưởng th&agrave;nh.</p>\r\n<p>&nbsp;</p>','2022-05-31 15:16:16.488000','p82b82241-5864-443d-8f67-83cdb6723471.jpg','LỜI TẠM BIỆT CỦA BUỔI CHÀO CỜ CUỐI CÙNG ',5);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (27,'<p dir=\"ltr\">15/05/2022 16:07:23</p>\r\n<p dir=\"ltr\">\"gửi m...</p>\r\n<p dir=\"ltr\">t với m chơi với nhau cũng đ&atilde; được một khoảng thời gian tuy kh&ocirc;ng d&agrave;i nhưng cũ đủ để biết phần n&agrave;o t&iacute;nh c&aacute;ch của nhau.t biết m kh&ocirc;ng th&iacute;ch việc ph&ocirc; trương v&agrave; kh&ocirc;ng th&iacute;ch những nơi của \"\"mọi sự ch&uacute; &yacute;\"\" nhưng xin lỗi m cho t xin ph&eacute;p được một lần c&oacute; thể n&oacute;i hết những g&igrave; trong l&ograve;ng t. M l&agrave; một đứa lu&ocirc;n vui vẻ, năng nổ trong c&ocirc;ng việc, hay gi&uacute;p đỡ người kh&aacute;c n&ecirc;n đ&ocirc;i l&uacute;c biến th&agrave;nh lo việc bao đồng lắm, lu&ocirc;n l&agrave; đứa đi đầu trong mọi nhiệm vụ hay cuộc chơi của nh&oacute;m, của lớp v&agrave; cực th&iacute;ch thể thao. Nhưng đ&acirc;y l&agrave; m của trước kia. Trong một năm trở lại đ&acirc;y m l&agrave; một c&ograve;n người kh&aacute;c, đ&atilde; kh&ocirc;ng c&ograve;n nhộn nhịp như trước, kh&ocirc;ng c&ograve;n cởi mở, bắt đầu kh&eacute;p m&igrave;nh hơn v&agrave; ngay cả sở th&iacute;ch của m&igrave;nh m&agrave; m c&ograve;n gh&eacute;t n&oacute;. Một năm qua m đ&atilde; phải đối mặt với rất nhiều chuyện, giải quyết bao rắc rối v&acirc;y quanh m v&agrave; đ&atilde; kh&oacute;c rất nhiều lần trước mặt mọi người. M trở n&ecirc;n ti&ecirc;u cực, ch&aacute;n nản v&agrave; mệt mỏi.Từ một đứa cười đ&ugrave;a, chạy nhảy mỗi ng&agrave;y th&igrave; giờ lu&ocirc;n giữ b&ecirc;n m&igrave;nh những vi&ecirc;n thuốc.M đ&atilde; phải trải qua khoảng thời gian tăm tối nhất v&agrave; ngay cả những người th&acirc;n như gia đ&igrave;nh hay thậm ch&iacute; bạn th&acirc;n của m m cũng kh&ocirc;ng d&aacute;m chia sẻ nhiều v&igrave; m sợ mọi người sẽ suy nghĩ v&agrave; đ&ocirc;i l&uacute;c sẽ th&agrave;nh \"\"sự thương hại\"\". V&agrave; c&aacute;ch duy nhất m l&agrave;m đ&oacute; l&agrave; chia sẻ cảm x&uacute;c của m&igrave;nh tr&ecirc;n story, đ&ocirc;i l&uacute;c t cảm thấy rất kh&oacute; chịu v&igrave; m đăng qu&aacute; nhiều m&agrave; chả n&oacute;i với ai c&acirc;u n&agrave;o. Thật kh&oacute; để m mới c&oacute; thể đối mặt với những kh&oacute; khăn đ&oacute; v&agrave; may mắn đến hiện tại m đ&atilde; cười nhiều hơn rồi, dần quay lại như trước kia.T đ&atilde; l&acirc;u rồi kh&ocirc;ng n&oacute;i chuyện với m nhưng vẫn d&otilde;i theo m n&ecirc;n t mong rằng m vẫn sẽ mai l&agrave; m, c&aacute;i c&ocirc; b&eacute; m lu&ocirc;n hớn hở với mọi người rằng l&agrave; thi&ecirc;n thần , bỏ ngo&agrave;i tai những lời n&oacute;i kh&ocirc;ng hay về m&igrave;nh v&agrave; sống t&iacute;ch cực l&ecirc;n. Đừng cố n&eacute;n cảm x&uacute;c của m&igrave;nh h&atilde;y cứ kh&oacute;c đi đừng sợ v&agrave; phải ch&uacute; &yacute; tới sức khỏe của m&igrave;nh. Kh&ocirc;ng ổn nhưng miệng lu&ocirc;n n&oacute;i ổn rồi kh&uacute;c sau lại than với mọi người rằng t vừa như thế...Sống cho bản th&acirc;n m&igrave;nh đ&atilde; rồi hẵng nghĩ cho người kh&aacute;c, đừng ti&ecirc;u cực nữa v&agrave; phải mạnh mẽ để đương đầu với những thử th&aacute;ch ở ph&iacute;a trước v&igrave; mục ti&ecirc;u của m đang đề ra chưa ho&agrave;n th&agrave;nh được c&aacute;i chi m&ocirc;. H&atilde;y \'ồn &agrave;o\"\", \"\"lắm chuyện\"\" như trước đi :&gt;</p>\r\n<p dir=\"ltr\">To: Rat317\"</p>\r\n<p>&nbsp;</p>','2022-05-31 15:17:58.263000','p55a5fb81-a43e-43da-a942-4c42f5f42189.jpg','Gửi cậu',5);
INSERT INTO `blogs` (`blog_id`,`content`,`created_at`,`image_title`,`title`,`user_id`) VALUES (28,'<p dir=\"ltr\">Ng&agrave;y h&ocirc;m nay,nếu ai đ&oacute; cảm thấy mệt mỏi,họ sẽ t&igrave;m gia đ&igrave;nh người th&acirc;n,anh chị để chia sẻ,động vi&ecirc;n hay tự an ủi ch&iacute;nh m&igrave;nh bằng những b&agrave;i h&aacute;t buồn v&agrave; t&ocirc;i cũng từng như vậy. <img src=\"https://lh6.googleusercontent.com/I9K4Oc4itph6XkwhjMkcDqMpYx-s6VuVgzptYbAcnjxr0meYwJscICuoZ8f1fB9pgxAxKfCBBi070DFYQv40LOAZ4GHhQr3OvCcfgOAb7YjjVvgGTD2-TxyR_4tJOO0086hg6x-x5Q9XueX7Jw\" alt=\"☹️\" width=\"16\" height=\"16\">Mỗi khi c&ocirc; đơn,t&ocirc;i cũng t&igrave;m đến bố mẹ,bạn b&egrave; nhưng t&ocirc;i vẫn cảm thấy trống trải trong l&ograve;ng m&igrave;nh cho đến khi t&ocirc;i gặp cậu với nụ cười tỏa nắng đ&oacute;.<img src=\"https://lh6.googleusercontent.com/qIfcDyTO73dWScN2uLxpGClslOVqkJXY4sKf1Nj8T45wov3bhu9MF7OTG0sQWRPpKD9TbwZOQRBr_x3r4iE-KUjegrGGFyB2hg3oT321NViyaJnNyKdvEsoA67f0h3W9JuZr7ykVytuZX3wPSw\" alt=\"🤩\" width=\"16\" height=\"16\"></p>\r\n<p dir=\"ltr\">T&ocirc;i v&agrave; cậu quen biết nhau chưa l&acirc;u,gặp nhau lần đầu ti&ecirc;n từ buổi học th&ecirc;m online,cậu l&agrave; người bắt chuyện trước với t&ocirc;i,gi&uacute;p t&ocirc;i l&uacute;c t&ocirc;i kh&ocirc;ng biết trả lời c&acirc;u hỏi mặc d&ugrave; ch&uacute;ng ta chưa hề biết nhau trước đ&oacute;. <img src=\"https://lh6.googleusercontent.com/6yFQFLYz2g6mHAE2UkrgWfUpvFpn8TKq2zdFFaVmcNiKl3vVXC-MNhHPmtzO0bUC1tHnKKm0KiZ0547_2PCIECX7lV2ehOKP8C80qvWXsMOR2aj08aJzM41WLiRdcWyDflIq4jhP1FqkqqumFQ\" alt=\"🧐\" width=\"16\" height=\"16\">L&uacute;c đ&oacute; t&ocirc;i chỉ nghĩ đơn giản l&agrave; m&igrave;nh gặp được 1 người tốt,thế rồi t&ocirc;i chủ động kết bạn facebook với cậu,nhắn tin cảm ơn cậu v&agrave; t&igrave;nh bạn của t&ocirc;i v&agrave; cậu bắt đầu từ đ&oacute;....<img src=\"https://lh3.googleusercontent.com/cs4Q7qh-pJs0zwn1c0bT1dfaE9nKYi-ezbsZW9UCOXfUlff7-knLpfeamxJt6EwgfrefEZII7EdGDl-HzNpoKRMPQ4qlrhIpPn4IxS9ebf3LKf9s4S-JnC15clpRHMZQwx6TdLsSJ85vJsl2MQ\" alt=\"🤗\" width=\"16\" height=\"16\"></p>\r\n<p dir=\"ltr\">Ch&uacute;ng t&ocirc;i trao đổi b&agrave;i với nhau,c&ugrave;ng nhau chia sẻ niềm vui,nỗi buồn,n&oacute;i chuyện tr&ecirc;n trời dưới đất <img src=\"https://lh4.googleusercontent.com/I9RwVbyuJtV8dN7u35eWRCVfRqjXqo3xXISPdQk6uCs2o5DeTDuPA5pKYmBWeG3ud_KtEu81_AipLyroGdfV70A_68sjhuImlJ7R6lrlbKW85VgYizUkkdHq0U0RBQQTJZbEdYx9hrHBTGpA7w\" alt=\"🙄\" width=\"16\" height=\"16\"> bởi lẽ t&iacute;nh c&aacute;ch của 2 đứa cũng kh&aacute; hợp nhau n&ecirc;n gặp nhau ở đ&acirc;u l&agrave; n&oacute;i chuyện ở đ&oacute;<img src=\"https://lh3.googleusercontent.com/VB2HizJlhHIcrtXKqaNGTYbJUoqwxTjzeEgb1QmTI1evXH1SfQBe3BmtTl2FgcgEKRnJj9CuDQzwC5nw6gKfygI_PF7wKrsVD0jrRVjBnLgKl_c5cjwf_Ml6kt4b7Kw-vQbUHTIDpo1xxQRDFA\" alt=\"😅\" width=\"16\" height=\"16\">. Cậu từng n&oacute;i với t&ocirc;i rằng &ldquo; Mi l&agrave; đứa con g&aacute;i đầu ti&ecirc;n v&agrave; duy nhất t n&oacute;i chuyện thoải m&aacute;i v&agrave; cởi mở,từ trước đến nay t chưa th&acirc;n với đứa con g&aacute;i n&agrave;o cả&rdquo;. <img src=\"https://lh4.googleusercontent.com/wbf0pFNkzFfkdh7CouP2v2_z_Cw2Uosgmpht_1qfcPif2saIHUH-Wvw_ZqTJkY9SKnyDsNY-cUtuv0xbPyoKdW8JsKuHA3TEb58KFWN0MPhxhF06LVoYu1piVHNg8-DR2GPa4spBesAMPNqZlQ\" alt=\"🤫\" width=\"16\" height=\"16\">Từ c&acirc;u n&oacute;i đ&oacute; ch&uacute;ng t&ocirc;i đ&atilde; trở th&agrave;nh bạn th&acirc;n kh&aacute;c giới,quan t&acirc;m nhau hơn,tr&ecirc;u đ&ugrave;a mọi l&uacute;c mọi nơi m&agrave; kh&ocirc;ng ngại khoảng c&aacute;ch,giới t&iacute;nh...<img src=\"https://lh5.googleusercontent.com/hYIH3-ftl_mrdmmRhvZEZ48-9cKqq2sF1_czerQFQRSYcphie-56IKOM3zMbz3-ooHxhvB8hVMIaur4nq0DnS05rJ1-i0YYGrr_YcGv1Y_XrvUlVlH4wpawUuGbwm_K-VE_7MVOC66nlxA-sqA\" alt=\"😳\" width=\"16\" height=\"16\"></p>\r\n<p dir=\"ltr\">Vốn dĩ tưởng rằng t&igrave;nh bạn giữa t&ocirc;i v&agrave; cậu sẽ tốt đẹp hết 3 năm cấp 3 cho đến 1 ng&agrave;y cậu khoe với t&ocirc;i rằng cậu th&iacute;ch 1 bạn trong lớp t&ocirc;i<img src=\"https://lh4.googleusercontent.com/IlbAOJlDJsC8s6fYkROhyWirBrrvJ2AH0iNcCTcWgfnCGXl7WJ0iUU_RbIibV-U_OT5iucBuysTjwx2K3c7---IvFu5d8aSesARe1bGS-PX5tU2cXVAh6Ulk2cVQVxBuIwG2IkUyk8m_ENngsQ\" alt=\"🥺\" width=\"16\" height=\"16\">. Nếu l&agrave; người con trai kh&aacute;c,t&ocirc;i sẽ h&oacute;ng ha h&oacute;ng hớt t&igrave;m hiểu c&aacute;c thứ nhưng đến lượt cậu,tr&aacute;i tim t&ocirc;i bỗng chợt nh&oacute;i l&ecirc;n 1 c&aacute;ch k&igrave; lạ v&agrave; giọt nước mắt của t&ocirc;i lăn xuống. <img src=\"https://lh5.googleusercontent.com/cDXXFZ0emd8N-8aUhfTrVM6ysmqHqXTbVjAzjB1rykisDQFnu7nWi-bdnnDrJVTve27aEn_WlGbhbGk_dZzATVta3nVXWP3_08dyzEKXyYN76mXcH9B6wS9ZPuzRB4wMwadzZTTw-tYikYJKVw\" alt=\"😔\" width=\"16\" height=\"16\">Cậu biết kh&ocirc;ng l&uacute;c đ&oacute; t&ocirc;i đ&atilde; nghĩ t&ocirc;i bị đi&ecirc;n rồi,tự nhi&ecirc;n lại kh&oacute;c v&ocirc; cớ đ&aacute;ng lẽ phải tr&ecirc;u khi bạn th&acirc;n m&igrave;nh c&oacute; crush nhưng sau đ&oacute; t&ocirc;i đ&atilde; rất kh&oacute; chịu mỗi lần cậu nhắc đến bạn nữ ấy- người con g&aacute;i c&oacute; sắc đẹp,t&agrave;i năng. <img src=\"https://lh6.googleusercontent.com/uDnH_HBuVanT0ZRmyqIEEVweZ-pHjbUa5oHGszu_v2pswfybGWgeQMASzWZyUgB1rV0MWjYzEqLXY-O4M9nx5QheW10aA0f7sepKRyEAvTrZlePXqdiaiXd2bNHmKLioU2vf_UH4v9MVHuXOiA\" alt=\"😫\" width=\"16\" height=\"16\"></p>\r\n<p dir=\"ltr\">T&ocirc;i đ&atilde; lỡ th&iacute;ch cậu rồi,t&ocirc;i đ&atilde; suy nghĩ rất nhiều l&agrave; l&agrave;m sao t&ocirc;i c&oacute; thể vượt c&ocirc; ấy để c&oacute; được cậu,chẳng phải t&ocirc;i l&agrave; người hiểu r&otilde; cậu hơn c&ocirc; ấy sao? <img src=\"https://lh4.googleusercontent.com/n8gwC7IslNVvP6MQLU9SztTtsPA9v1V6tcBr6Cvxh0VRwBFJpcZ67v7Egv8Buhm9wsCjx_DOE1BqnEz0MtUfML_N4q4zDHi_IFQtaVzsC1bQam1JvpKk4CclZrJ_p-kNgDh0sxqDtO4iVI3npw\" alt=\"🤔\" width=\"16\" height=\"16\">H&agrave;ng trăm c&acirc;u hỏi xoay quanh đầu t&ocirc;i m&agrave; kh&ocirc;ng c&oacute; đ&aacute;p &aacute;n cho đến h&ocirc;m nay t&ocirc;i muốn n&oacute;i với cậu 1 điều l&agrave; &ldquo; T&ocirc;i muốn được &ocirc;m chặt cậu,d&ugrave; biết tr&aacute;i tim cậu đang gọi t&ecirc;n người kh&aacute;c.Bởi lẽ t&ocirc;i l&agrave; người yếu đuối n&ecirc;n t&ocirc;i muốn dựa v&agrave;o vai cậu m&atilde;i,d&ugrave; biết cậu chỉ xem t&ocirc;i l&agrave; bạn th&acirc;n&rdquo; <img src=\"https://lh4.googleusercontent.com/CGZUkxNUmCWSck7AwYtLaWVGuA27reaIhtYoUk7H7v1nKbbbEa1NjOJeETP4AXiHkNhhhmslrdcjE31sek7rr_snlvDbuDC7BoJOISm_bIegJSyVeba3B1udCSnOeoCY-WX2YfctcC6f7gnTWA\" alt=\"😶\" width=\"16\" height=\"16\"></p>\r\n<p dir=\"ltr\">T&ocirc;i sẽ kh&ocirc;ng ph&aacute; đ&aacute;m cậu,cũng kh&ocirc;ng ngăn cản cậu nhưng t&ocirc;i chỉ muốn cậu m&atilde;i l&agrave; người bạn th&acirc;n kh&aacute;c giới của t&ocirc;i c&ograve;n t&igrave;nh cảm t&ocirc;i sẽ giữ 1 m&igrave;nh...<img src=\"https://lh6.googleusercontent.com/Or_MVv2lJieyVNmX5ZzYGhfXsjVuo8cqOnetule1UHIQaM1l9nS3fPCpuiF7s4lcGhhxaoF1ZpEAmjM98XOzDH2Be_Cp5htO4lw7XqgZfQVwcGt7g17zwiHByi398eiFNEMl9IJUTDywUKoyuA\" alt=\"🤭\" width=\"16\" height=\"16\"></p>\r\n<p dir=\"ltr\">Th&acirc;n gửi bạn th&acirc;n fake =))</p>\r\n<p>&nbsp;</p>','2022-05-31 15:19:12.695000','p022f6c43-fb7d-4efe-8ec7-0688ce2d9d5f.jpg',' Gửi bạn từng thân của tôi',5);
